 async (props: {
  /** the page element from puppeteer got with browser.pages */
  page: Page;
  /** the css selector to click */
  selector: string;
}): Promise<{ success: boolean }> => {
  try {
    const { page, selector } = props;
    await page.evaluate((sendButtonSelector: string) => {
      let a = document.querySelector(sendButtonSelector);
      (a as HTMLElement).click?.();
    }, selector);
    return {
      success: true,
    };
  } catch (err) {
    return {
      success: false,
    };
  }
}
