 async (props: {
  page: Page;
  password: string;
  username: string;
}) => {
  const { page, password, username } = props;
  await page.goto("https://twitter.com/login");
  // Adding listener for console
  page.on("console", (message) => console.log(`${message.text()}`));
  // wait for username input field to appear
  await typeInTheInputField({
    page,
    selector: 'input[autocomplete="username"]',
    value: username,
  });

  // clicking next button
  await page.click(
    'div[class="css-18t94o4 css-1dbjc4n r-sdzlij r-1phboty r-rs99b7 r-ywje51 r-usiww2 r-2yi16 r-1qi8awa r-1ny4l3l r-ymttw5 r-o7ynqc r-6416eg r-lrvibr r-13qz1uu"]'
  );

  // wait for the password field to appear
  await typeInTheInputField({
    page,
    selector: 'input[autocomplete="current-password"]',
    value: password,
  });
  // waiting and clicking on the login button
  await page.waitForSelector(
    'div[class="css-901oao r-1awozwy r-6koalj r-18u37iz r-16y2uox r-37j5jr r-a023e6 r-b88u0q r-1777fci r-rjixqe r-bcqeeo r-q4m81j r-qvutc0"]'
  );
  await page.click(
    'div[class="css-901oao r-1awozwy r-6koalj r-18u37iz r-16y2uox r-37j5jr r-a023e6 r-b88u0q r-1777fci r-rjixqe r-bcqeeo r-q4m81j r-qvutc0"]'
  );
}
