async () => {
  const operationFolderPath = await getOperationPath("puppeteer-utils");
  console.log("Child Process called", operationFolderPath);
  const browserSession = await db.get("BrowserSession");
  if (browserSession.length === 0) {
    const newProcess = fork(
      `${operationFolderPath}/build/cli/runBrowser.cli.js`,
      {
        detached: true,
        // stdio: "ignore",
      }
    );
    newProcess.unref();
  }
}
