async (
  pageId?: string
): Promise<{ page?: Page; pageId: string }> => {
  const browser = await openNewBrowser();
  if (pageId) {
    return { page: getBrowserPage(pageId), pageId };
  }

  const page = await browser.newPage();
  await page.setBypassCSP(true);
  const cachePageId = setBrowserPage(page);
  return { pageId: cachePageId, page };
}
