async (props: {
  page: Page;
  selector: string;
  value: string;
}) => {
  const { page, selector, value } = props;
  console.log("select in fnc", selector, value);
  await page.evaluate(
    ({ value, selector }) => {
      const h3Element: ElementType | null = document.querySelector(
        `${selector}`
      );
      if (h3Element) {
        h3Element.click?.();
        h3Element.focus?.();
        h3Element.innerHTML = value;
      }
    },
    { value, selector }
  );
}
