async (props: {
  page: Page;
  selector: string;
  value: string;
}) => {
  const { page, selector, value } = props;
  // wait for selector
  await page.waitForSelector(selector, { timeout: 30000 });
  // click, focus and enter the value
  await page.click(selector);
  await page.focus(selector);
  // throw 'test message';
  await page.keyboard.type(value);
}
