async (props: {
  page: Page;
  selector: string;
  text: string;
}) => {
  const { page, selector, text } = props;
  const targetedElement = await page.$x(selector);

  if (targetedElement[0]) {
    await targetedElement[0].focus();
    //@ts-ignore
    await targetedElement[0].click();
    console.log(
      "selector find and click",
      await (await targetedElement[0].getProperty("role")).jsonValue()
    );
  }
  console.log("Aout to type", text);
  await page.keyboard.type(text);
}
