export const readMarkdownFileToModel = async (
  absoluteFilePath: string
): Promise<null | WebMarkdownFile> => {
  const filename = path.parse(absoluteFilePath).name;

  const projectRoot = getProjectRoot(absoluteFilePath);

  if (!projectRoot) return null;
  const mdParse = await readMarkdownFile(absoluteFilePath);
  if (!mdParse) return null;

  const parsedParameters = frontmatterToObject(
    mdParse.parameters,
    webMarkdownFileTsInterface.type.simplifiedSchema as SimplifiedSchema
  ) as Partial<
    ExtendedMarkdownProperties & WebMarkdownProperties & PostableProperties
  >;

  // NB: this markdown file is not always located in an operation. Yet we are trying to find it.
  const operationBasePath = findOperationBasePath(absoluteFilePath);
  const modelLocation: ModelLocation = {
    operationName: operationBasePath ? getLastFolder(operationBasePath) : null,
    projectRelativePath: makeRelative(absoluteFilePath, projectRoot),
    operationRelativePath: operationBasePath
      ? makeRelative(absoluteFilePath, operationBasePath)
      : undefined,
  };

  const name = mdParse.fileName || "untitled";
  const markdownFile: WebMarkdownFile = {
    ...modelLocation,
    ...parsedParameters,
    markdown: mdParse.raw,
    categoryStackCalculated: [],
    ...parsedParameters,
    id: name,
    createdFirstAt: Date.now(),
    deletedAt: 0,
    createdAt: Date.now(),
    privacy: parsedParameters.privacy || "public",
    language: parsedParameters.language || "english",
    updatedAt: parsedParameters.updatedAt || Date.now(),
    name,
    slug: name,
  };

  return omitUndefinedValues(markdownFile);
};
