 async ({
  operationFolderPath,
  pathSuffix,
  extension,
}: {
  operationFolderPath: string;
  extension?: SearchableExtension | SearchableExtension[];
  pathSuffix: string;
}) => {
  const basePath = path.join(operationFolderPath, pathSuffix);

  return (
    await explore({
      basePath,
      extension,
    })
  )
    .map((x) => x.path)
    .map((p) => withoutExtension(p.slice(basePath.length)));
}
