 async ({
  operationFolderName,
  scriptFileName,
  args,
}: {
  operationFolderName: string;
  /**
   * the location of the script in the build folder
   * NB: this must be a CLI that that actually executes the function!
   */
  scriptFileName: string;
  args?: string[];
}) => {
  const childOperationPath = await getOperationPath(operationFolderName);
  if (!childOperationPath) {
    log(
      `Couldn't find childOperationPath for operation ${operationFolderName}`,
      { type: "error" }
    );
    return;
  }
  const scriptPath = path.join(
    childOperationPath,
    `build/${scriptFileName}.js`
  );

  if (!fs.existsSync(scriptPath)) {
    log(
      `Couldn't find scriptFile ${scriptFileName} which should be at build/${scriptFileName}.js`,
      { type: "error" }
    );
    return;
  }

  const readable = await canRead(scriptPath);

  if (!readable) {
    log(`Can't read ${scriptPath}`, { type: "error" });
    return;
  }

  const command = `node ${scriptPath} ${args?.join(" ") || ""}`;

  // log(`going to execute: ${command}`, { type: "important" });

  const result = spawnSync(command, {
    shell: true,
    stdio: "inherit",
    encoding: "utf8",
  });

  return result.output;
}
