 async (
  functionContext: FunctionContext,
  newCurentPersonId: string
): Promise<{ isSuccessful: boolean; message: string }> => {
  if (functionContext.device.currentPersonId === newCurentPersonId) {
    return { isSuccessful: false, message: "This account is already active" };
  }

  const found = functionContext.device.personIds?.find(
    (x) => x === newCurentPersonId
  );

  if (!found) {
    return { isSuccessful: false, message: "Can't find that account" };
  }

  const { isSuccesful, message } = await db.update(
    "Device",
    (item) => item.id === functionContext.device.id,
    (old) => ({ ...old, currentPersonId: newCurentPersonId })
  );

  return {
    isSuccessful: isSuccesful || false,
    message: isSuccesful
      ? "Switched"
      : message || "Something went wrong (no message)",
  };
}
