 async (
  functionContext: FunctionContext,
  details: PersonProfileDetails
): Promise<{ isSuccessful: boolean; message: string }> => {
  const person = functionContext.device.currentPersonCalculated;

  if (!person) {
    return {
      isSuccessful: false,
      message: "Couldn't find you, are you logged in?",
    };
  }

  const { isSuccesful, message, amountUpdated } = await db.update(
    "Person",
    (item) => item.id === person.id,
    (old) => ({ ...old, ...details })
  );

  const isReallySuccessful = (isSuccesful && amountUpdated === 1) || false;

  return {
    isSuccessful: isReallySuccessful,
    message: isReallySuccessful
      ? "Updated your profile details"
      : message || "Something went wrong (no message)",
  };
}
