export const getOrGenerateShortMarkdown = async (
  projectRelativeMarkdownFilePath: string
): Promise<ShortMarkdown | null> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return null;

  const absoluteFilePath = path.join(
    projectRoot,
    projectRelativeMarkdownFilePath
  );

  const fileParse = path.parse(absoluteFilePath);

  const indexFilePath = path.join(fileParse.dir, `${fileParse.name}.json`);

  if (!fs.existsSync(indexFilePath)) {
    const shortMarkdown = await generateAugmentedShortMarkdown(
      projectRelativeMarkdownFilePath
    );

    return shortMarkdown || null;
  }

  const shortMarkdown = await readJsonFile<ShortMarkdown>(indexFilePath);

  return shortMarkdown;
};
