export const SwipeHomepage = (props: {
  ctas: { text: string; href: string }[];
  items: SwipeItem[];
}) => {
  const { ctas, items } = props;
  const buttonClass =
    "bg-white/60 rounded-full hover:bg-white cursor-pointer text-black p-2";
  return (
    <Div>
      <Div className="h-screen overflow-auto snap-mandatory snap-y">
        {items.map((item, index) => {
          const nameSizeClass =
            item.title.length > 10 ? "text-2xl" : "text-6xl";
          return (
            <Div
              key={`item${index}`}
              style={{
                backgroundImage: item.image
                  ? `url("${item.image}")`
                  : undefined,
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                backgroundPosition: "center 75%",
              }}
              className="snap-center h-screen w-screen flex flex-col justify-center relative items-center"
            >
              <ALink
                href={item.href || "#"}
                className={`absolute top-20 right-20 text-white font-bold drop-shadow bg-green-500/20 rounded-md px-4 py-2 backdrop-blur-sm ${nameSizeClass} ${
                  item.href ? "cursor-pointer" : "cursor-default"
                }`}
              >
                {item.title}
              </ALink>
              <ALink
                href={item.href || "#"}
                className={`absolute bottom-10 mx-4 rounded-md text-white p-6 bg-white/30 backdrop-blur-sm ${
                  item.href ? "cursor-pointer" : "cursor-default"
                }`}
              >
                {item.description}
              </ALink>
            </Div>
          );
        })}
      </Div>
      <Div className="z-index-1 absolute bottom-4 w-full flex flex-row justify-around">
        {ctas.map((cta) => {
          return (
            <ALink href={cta.href} className={buttonClass}>
              {cta.text}
            </ALink>
          );
        })}
      </Div>

      <Div className="z-index-1 absolute bg-white/60 rounded-full w-12 h-12 top-4 left-4">
        <Image src="/logo.png" width={50} height={50} />
      </Div>
    </Div>
  );
};
