export const Tabs = (props: { title?: string; tabs: TabType[] }) => {
  const { title, tabs } = props;

  const [currentTab, setCurrentTab] = useStore("tabs.currentTab");

  const buttons: LabeledButtonType[] = tabs.map(({ emoji, title }, index) => {
    const isActive = currentTab === index;
    return {
      emoji,
      title,
      onClick: () => {
        setCurrentTab(index);
      },
      isActive,
    };
  });

  const activeTab = tabs.find((_, index) => currentTab === index);

  return (
    <Div className="flex flex-col flex-1 p-2 m-2 rounded-xl">
      <Div className="flex flex-row justify-between items-center">
        {title ? <P className="text-3xl">{title}</P> : null}
        <Div className="flex flex-row gap-3">{buttons.map(LabeledButton)}</Div>
      </Div>

      {activeTab?.renderTab()}
    </Div>
  );
};
