rawText:
 (props: { items: TimelineItemType[] }) => {
  const { items } = props;
  return (
    <div className="flex snap-x snap-mandatory h-screen w-full mx:auto overflow-scroll">
      {items.map((item, index) => {
        return (
          <Div
            key={`timeline-item-${index}`}
            className="snap-start shrink-0 bg-green-200 w-screen h-screen place-items-center"
            style={{
              backgroundImage: item.imageUrl
                ? `url("${item.imageUrl}")`
                : undefined,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "center 75%",
            }}
          >
            {item.component ? (
              item.component()
            ) : (
              <Div
                className="max-w-[80vw]"
                onClick={() => {
                  console.log("supposed to open", item);
                  api.vscodeOpen({
                    files: [
                      {
                        projectRelativePath: item.projectRelativeFilePath,
                        line: item.line,
                      },
                    ],
                  });
                }}
              >
                <MarkdownCodeblock
                  text={item.markdown || "Nothing to show"}
                  isModeStatic
                  minimalMode="scroll"
                  extension="tsx"
                />
              </Div>
            )}
          </Div>
        );
      })}
    </div>
  );
}
