rawText:
 (props: { items: TimelineItemType[] }) => {
  const { items } = props;
  return (
    <Div className="h-screen overflow-auto snap-mandatory snap-y">
      {items.map((item) => {
        return (
          <Div
            className="snap-center h-screen w-screen flex justify-center items-center"
            style={{
              backgroundColor: pickRandomArrayItem(colors),
              backgroundImage: item.imageUrl
                ? `url("${item.imageUrl}")`
                : undefined,
              backgroundRepeat: "no-repeat",
              backgroundSize: "cover",
              backgroundPosition: "center 75%",
            }}
          >
            {item.component ? (
              item.component()
            ) : (
              <Div
                className="max-w-[80vw]"
                onClick={() => {
                  console.log("supposed to open", item);
                  api.vscodeOpen({
                    files: [
                      {
                        projectRelativePath: item.projectRelativeFilePath,
                        line: item.line,
                      },
                    ],
                  });
                }}
              >
                <MarkdownCodeblock
                  text={item.markdown || "Nothing to show"}
                  isModeStatic
                  minimalMode="scroll"
                  extension="tsx"
                />
              </Div>
            )}
          </Div>
        );
      })}
    </Div>
  );
}
