 (operationFolderPath: string) => {
  const tsConfigFilePath = path.join(operationFolderPath, "tsconfig.json");

  const tsConfigExists = fs.existsSync(tsConfigFilePath);
  const srcExists = existsSync(path.join(operationFolderPath, "src"));
  if (!tsConfigExists || !srcExists) {
    log(
      `This is not an operation: ${operationFolderPath}. (src folder or tsconfig.json are not present)`,
      { type: "error" }
    );
    return;
  }

  const project = new Project({ tsConfigFilePath, libFolderPath: "src" });

  return project;
}
