 (manualProjectRoot?: string) => {
  setInterval(() => {
    try {
      // NB: This doesn't work well with nested .git folders!
      execSync(
        "[[ `git status --porcelain .` ]] && git add . && git commit -m 'Automatic commit'",
        { cwd: manualProjectRoot, stdio: "inherit" }
      );
    } catch {}
  }, 1000 * 3600);
}

