const pushPosition = async () => {
const position = await getLocation();
if (position) {
push("Position", {
...position,
createdAt: Date.now(),
updatedAt: Date.now(),
createdFirstAt: Date.now(),
deletedAt: 0,
id: generateId(),
});
}
};

const pushLight = async () => {
const light = 1;
if (light) {
push("Light", {
id: generateId(),
createdFirstAt: Date.now(),
createdAt: Date.now(),
updatedAt: Date.now(),
deletedAt: 0,
light,
});
}
};

const watchLocation = async () => {
pushPosition();
setInterval(() => {
pushPosition();
}, 60000);
};

const watchLight = async () => {
pushLight();
setInterval(() => {
pushLight();
}, 60000);
};
