async (
  projectRelativePath: string,
  newName: string
): Promise<{ isSuccessful: boolean; message?: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return { isSuccessful: false, message: "No project root found" };
  }

  const absolutePath = path.join(projectRoot, projectRelativePath);

  if (!fs.existsSync(absolutePath)) {
    return {
      isSuccessful: false,
      message: "This file doesn't exist",
    };
  }

  const absoluteFolderPath = path.parse(absolutePath).dir;

  const newAbsolutePath = path.join(absoluteFolderPath, newName);

  if (fs.existsSync(newAbsolutePath)) {
    return { isSuccessful: false, message: "File/folder already exists" };
  }

  await fs.rename(absolutePath, newAbsolutePath);

  return { isSuccessful: true };
}
