export const WriterInput = (props: {
  /**
   * boolean indicating whether or not the file has already been saved
   *
   * If false, save button will be shown
   */
  isSaved?: boolean;
  /**
   * function to save the file
   *
   * If given, will render a button to save the file
   */
  save?: () => void;
  /**
   * Provide a MappedObject of `AugmentedWord`s here to allow for autocomplete intellisense type stuff
   */
  augmentedWordObject?: MappedObject<AugmentedWord>;
  /**
   * The text to edit
   */
  value: string;

  /**
   * can refer to a modelname in case you have frontmatter in that model that needs to be formified
   */
  markdownModelName?: keyof DbModels;
  /**
   * Callback called when text changes
   */
  onChange: (value: string) => void;
  /**
   * Force the type of content
   */
  type?: WriterType;
  /**
   * If you provide the path to the edited file here, it will infer the type of data in the file (typescript, markdown, etc) from it
   *
   * Besides this, it is required to show assets, as we need to know where the file is stored so we can know where the relative assets should be resolved.
   */
  projectRelativeFilePath?: string;
  /**
   * A function to reload the content
   *
   * If given, will show this button to reload the text manually
   */
  reload?: () => void;
  /**
   * Boolean indicating whether or not this thing is loading (content is still not there)
   */
  isLoading?: boolean;

  /**
   * if true, hides buttons
   */
  hideButtons?: boolean;
  /**
   * classname for the container
   */
  className?: string;

  /**
   * If given, forces the writerview to this value instead of using the current writerViewStore value
   */
  initialWriterView?: WriterViewEnum;
  disabledMenuItems?: string[];
}) => {
  const [writerView, setWriterView] = useStore("writerView");

  const {
    type,
    projectRelativeFilePath,
    reload,
    value,
    onChange,
    markdownModelName,
    initialWriterView,
    augmentedWordObject,
    isLoading,
    save,
    isSaved,
    hideButtons,
    className,
    disabledMenuItems,
  } = props;

  const projectRelativeBaseFolderPath = getFolderJs(projectRelativeFilePath);
  const projectRelativeMarkdownFilePath = projectRelativeFilePath;

  useEffect(() => {
    if (initialWriterView) {
      setWriterView?.(initialWriterView);
    }
  }, []);

  const filename = projectRelativeFilePath?.split("/").pop();
  const extension = filename?.split(".").pop();

  // Detect type either based on forced type, filename or on content (in descending order)
  const writerType: WriterType = type
    ? type
    : extension
    ? getWriterType(extension)
    : getWriterTypeFromContent(value);

  const icon =
    writerType === "markdown" ? "✍️" : writerType === "typescript" ? "⌘" : "📃";

  const canRenderWriterInput =
    projectRelativeBaseFolderPath !== undefined &&
    projectRelativeMarkdownFilePath !== undefined;

  const renderWriter = () => {
    const frontmatterSchemaQuery =
      queries.useGetFrontmatterSchema(markdownModelName);

    const markdownParse = mdToJsonParse(value, filename, {
      frontmatterSchema: frontmatterSchemaQuery.data?.result,
      noFinal: true,
    });

    const markdownParseRenderConfig: MarkdownParseRenderConfig = {
      augmentedWordObject,
      projectRelativeBaseFolderPath: projectRelativeBaseFolderPath || "",
      projectRelativeMarkdownFilePath: projectRelativeMarkdownFilePath || "",
      isDev,
      isStatic: false,
    };

    return (
      <Div className="flex flex-col flex-1 px-4">
        {writerView === "shortStudio" ? (
          <ShortStudio
            onChange={onChange}
            value={value}
            projectRelativeFilePath={projectRelativeFilePath || ""}
            markdownModelName={markdownModelName}
          />
        ) : null}
        {writerView === "postable" ? <Div>This is postable</Div> : null}
        {writerView === "todoOffers" ? <Div>This is todo offers</Div> : null}

        {writerView === "config" ? <WriterConfigForm /> : null}

        {writerView === "frontmatter" &&
        !frontmatterSchemaQuery.isLoading &&
        frontmatterSchemaQuery.data?.result &&
        projectRelativeMarkdownFilePath ? (
          <FrontmatterForm
            modelName={markdownModelName}
            key={projectRelativeMarkdownFilePath}
            markdownParse={markdownParse}
            projectRelativeMarkdownFilePath={projectRelativeMarkdownFilePath}
            frontmatterSchema={frontmatterSchemaQuery.data?.result}
            onChange={onChange}
          />
        ) : null}

        {writerView === "edit" || writerView === undefined ? (
          <EditWriterInput
            onChange={onChange}
            value={value}
            projectRelativeFilePath={projectRelativeFilePath || ""}
            markdownModelName={markdownModelName}
          />
        ) : null}

        {writerView === "view" || writerView === "presentation" ? (
          <MarkdownView
            view={writerView}
            markdownParse={markdownParse}
            markdownParseRenderConfig={markdownParseRenderConfig}
          />
        ) : null}
      </Div>
    );
  };

  const notDisabled = (menuName: string) => {
    return !disabledMenuItems || !disabledMenuItems.includes(menuName);
  };
  return (
    <Div className={`flex flex-col flex-1 ${className}`}>
      {hideButtons || !setWriterView ? null : (
        <TitleContainer
          buttons={[
            {
              onClick: () => save?.(),
              emoji: "💾",
              title: "Save",
              isEnabled: isSaved === false && !!save,
            },

            {
              onClick: () => setWriterView("edit"),
              emoji: "✏️",
              title: "Edit",
              isActive: writerView === "edit",
              isEnabled: notDisabled("edit"),
            },

            {
              onClick: () => setWriterView("view"),
              emoji: "👁",
              title: "View",
              isActive: writerView === "view",
              isEnabled: notDisabled("view"),
            },

            {
              onClick: () => {
                // TODO; ALSO MAKE FULLSCREEN HERE
                setWriterView("presentation");
              },
              emoji: "📽",
              title: "Present",
              isActive: writerView === "presentation",
              isEnabled:
                writerType === "markdown" && notDisabled("presentation"),
            },

            {
              onClick: () => {
                setWriterView("frontmatter");
              },
              emoji: "🧩",
              title: "About",
              isActive: writerView === "frontmatter",
              isEnabled:
                writerType === "markdown" &&
                !!markdownModelName &&
                notDisabled("frontmatter"),
            },

            {
              onClick: () => {
                setWriterView("config");
              },
              emoji: "⚙️",
              title: "Configuration",
              isActive: writerView === "config",
              isEnabled: notDisabled("config"),
            },

            {
              onClick: () => {
                setWriterView("postable");
              },
              emoji: "💬",
              title: "Postables",
              isActive: writerView === "postable",
              isEnabled: projectRelativeFilePath?.endsWith(".postable.md"),
            },

            {
              onClick: () => {
                setWriterView("shortStudio");
              },
              emoji: "🎙",
              title: "Short Studio",
              isActive: writerView === "shortStudio",
              isEnabled: projectRelativeFilePath?.endsWith(".short.md"),
            },

            {
              onClick: () => {
                setWriterView("todoOffers");
              },
              emoji: "📖",
              title: "Todo Offers",
              isActive: writerView === "todoOffers",
              isEnabled:
                projectRelativeFilePath?.includes("/todo/") &&
                notDisabled("todo"),
            },

            {
              onClick: () => {
                vscodeOpen({
                  files: [{ projectRelativePath: projectRelativeFilePath }],
                });
              },
              emoji: "💻",
              title: "Code",
              isEnabled: projectRelativeFilePath !== undefined,
            },

            {
              isEnabled: !!reload,
              onClick: reload || (() => null),
              component: isLoading ? () => <FancyLoader /> : undefined,
              emoji: isLoading ? undefined : "↻",
              title: isLoading ? "Loading" : "Reload",
            },
          ]}
          title={filename ? `${icon} ${filename}` : undefined}
        >
          <Div />
        </TitleContainer>
      )}

      {canRenderWriterInput
        ? renderWriter()
        : "Can't render.... need `projectRelativeFilePath`"}
    </Div>
  );
};
