getAllSearchResults.rawText:
 async (
  query: string | null
): Promise<SearchResult[]> => {
  const projectRoot = getProjectRoot();

  if (!query || !projectRoot) return [];
  //  Alter search function to return all results from functions, files and db, and links to there.
  const functions = Object.keys(sdkFunctionPaths)
    .map((name) => {
      const path = sdkFunctionPaths[name as keyof typeof sdkFunctionPaths];

      return { name, path };
    })
    .filter((x) => {
      const names = `${x.name.toLowerCase()} ${humanCase(
        x.name
      ).toLowerCase()}`;
      return names.includes(query.toLowerCase());
    })
    .map((x) => {
      const result: SearchResult = {
        title: `‚ö°Ô∏è ${x.name}`,
        description: x.path,
        url: `/function/${x.name}`,
      };

      return result;
    });
  const files = (
    await explore({
      basePath: projectRoot,
      ignore: [
        ...generatedFolders,
        "db",
        "cloned",
        "backups",
        "bundled",
        "buntest",
      ],
      readmeOnTop: true,
      search: query || undefined,
      extension: ["md", "ts", "tsx"],
    })
  ).map((x) => {
    const relativePath = makeRelative(x.path, projectRoot);
    const result: SearchResult = {
      title: `${x.isFolder ? "üìÅ" : "üìÑ"} ${relativePath}`,
      url: `/files/${relativePath}`,
    };

    return result;
  });

  const dbModels = dbModelKeys
    .filter((x) =>
      `${x.toLowerCase()} ${humanCase(x).toLowerCase()}`.includes(
        query.toLowerCase()
      )
    )
    .map((x) => {
      const result: SearchResult = {
        title: `üóÑ ${x}`,
        url: `/db/${x}`,
      };
      return result;
    });

  return [...dbModels, ...functions, ...files];
}

getTimelineItems.rawText:
 async () => {
  const comments = await db.get("TsComment");

  const items = comments
    .filter((x) => x.types.includes("todo"))
    .map((x) => {
      const isMultiLine = x.firstLine !== x.lastLine;

      const operationPath =
        operations[x.operationName as keyof typeof operations];

      const comment = isMultiLine
        ? `/**\n${x.comment}\n*/\n\n${x.rawStatement}`
        : `// ${x.comment} \n ${x.rawStatement}`;

      const filePath = operationPath
        ? path.join(operationPath, x.operationRelativeTypescriptFilePath)
        : undefined;
      return {
        comment,
        filePath,
        line: x.firstLine,
      };
    });

  return items;
}

