copyCopyPairs.rawText:
 (copyPairs: CopyPair[]) => {
  const copyPromises = copyPairs.map(async (copyPair) => {
    const absoluteDestinationFolderPath = path.parse(
      copyPair.absoluteDestinationPath
    ).dir;

    // first make the folder if not already there
    if (!fs.existsSync(absoluteDestinationFolderPath)) {
      await fs.mkdir(absoluteDestinationFolderPath, { recursive: true });
    }

    await fs.cpAsync(
      copyPair.absoluteSourcePath,
      copyPair.absoluteDestinationPath,
      {
        preserveTimestamps: true,
      }
    );
  });

  return Promise.all(copyPromises);
}

copyReaderStaticAssets.rawText:
 async (
  operationBasePath: string,
  /**
   * non-reader webPages will be filtered out by checking if the projectRelativeFilePath exists on pageData and has the extension .md
   */
  allWebPages: WebPage<unknown>[]
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;

  const baseDestinationPath = path.join(
    operationBasePath,
    "public",
    "markdown-assets"
  );

  if (fs.existsSync(baseDestinationPath)) {
    //first clean up if it was generated before
    await fs.rm(baseDestinationPath, { recursive: true });
  }

  const readerWebPages = allWebPages
    .map((x) => ({
      ...x,
      pageData: x.pageData as null | { [key: string]: any },
    }))
    // filter out the WebPages that don't have a filePath ending on .md
    .filter((x) => x.pageData?.projectRelativeFilePath?.endsWith(".md"))
    .filter((x) =>
      fs.existsSync(path.join(projectRoot, x.pageData?.projectRelativeFilePath))
    )
    .map((x) => x as ReaderWebPage);

  console.log({ readerPagesThatExist: readerWebPages.length });
  const absoluteAssetPaths =
    (await findReaderStaticAssets(readerWebPages)) || [];

  console.log({ foundAssetsThatExist: absoluteAssetPaths.length });
  const copyPairs = absoluteAssetPaths.map((absoluteAssetPath) => {
    const projectRelativeAssetPath = makeRelative(
      absoluteAssetPath,
      projectRoot
    );

    const absoluteDestinationPath = path.join(
      baseDestinationPath,
      projectRelativeAssetPath
    );

    return { absoluteSourcePath: absoluteAssetPath, absoluteDestinationPath };
  });

  await copyCopyPairs(copyPairs);

  console.log(`Copied ${copyPairs.length} files into your public folder`);

  // console.log({ copyPairs });
}

findReaderStaticAssets.rawText:
 async (
  /**
   * Your markdown pages
   */
  readerWebPages: ReaderWebPage[]
) => {
  const projectRoot = getProjectRoot();

  if (!projectRoot) return;

  const absoluteAssetPaths = (
    await Promise.all(
      readerWebPages.map(async (page) => {
        const absoluteMarkdownFilePath = path.join(
          projectRoot,
          page.pageData.projectRelativeFilePath
        );

        const assetPaths = await findStaticAssets(absoluteMarkdownFilePath);

        return assetPaths;
      })
    )
  )
    .flat()
    .filter(onlyUnique2())
    .filter(fs.existsSync);

  return absoluteAssetPaths;
}

findStaticAssets.rawText:
 async (
  absoluteMarkdownFilePath: string
): Promise<string[]> => {
  if (!fs.existsSync(absoluteMarkdownFilePath)) return [];
  const content: string = await fs.readFile(absoluteMarkdownFilePath, "utf8");

  const relativeEmbeds = findEmbeds(content)
    .filter((x) => isPathRelative(x.src))
    .map((x) => x.src);

  const relativeLinks = findLinks(content)
    .filter(
      (x) => isPathRelative(x.href)
      // NB: bit shitty that we can't link to typescript files or pdfs this way, but it's ok, we can embed them. Once needed, this must be improved, not now.
    )
    .filter((x) => x.type !== "text" && x.type !== "other")
    .map((x) => x.href);

  const absoluteMarkdownFolderPath = getFolderJs(absoluteMarkdownFilePath);
  const absoluteAssetPaths = [relativeLinks, relativeEmbeds]
    .flat()
    .map((relativePath) => path.join(absoluteMarkdownFolderPath, relativePath));

  return absoluteAssetPaths;
}

addCodestoryToSection.rawText:
 (
  sectionContent: string | undefined,
  mappedObject: { [key: string]: CodespanItemInfo },
  isDebug?: boolean
): string | undefined => {
  if (!sectionContent) return;
  // For every piece of content find the codespans
  const codespans = findCodespans(sectionContent);

  const info = codespans
    .map((word) => {
      const details = (
        mappedObject as unknown as {
          [key: string]: CodespanItemInfo;
        }
      )[word];

      if (!details) return;

      // Augment every section with one <details> section for every referred piece of code.
      return writeCodespanDetails(details);
    })
    .filter(notEmpty);
  if (isDebug) {
    console.log({ codespans: codespans.length, infos: info.length });
  }

  return `${sectionContent}${
    info.length > 0 ? `\n\n${info.join("\n\n")}\n` : ""
  }`;
}

findCodestories.rawText:
 async (): Promise<string[]> => {
  // open the file
  const projectRoot = getProjectRoot();
  if (!projectRoot) return [];

  const allMarkdownPaths = await getProjectRelativePaths();

  const frontmatterObject = await getFrontmattersMappedObject(
    projectRoot,
    allMarkdownPaths
  );

  const stories =
    allMarkdownPaths?.filter((x) => {
      const frontmatter = frontmatterObject[x];
      if (!frontmatter) return false;

      const isCodestory = frontmatter.isCodestory === "true";
      return isCodestory;
    }) || [];

  const files = stories
    .filter((x) => !x.endsWith(".codestory.md"))
    .map((x) => x);

  return files.map((x) => path.join(projectRoot, x));
}

makeCodespanMappedObject.rawText:
 async () => {
  // a mapped object of all TsFunctions, TsInterfaces, Operations
  const tsVariables = await db.get("TsVariable"); // name -> description
  const tsInterfaces = await db.get("TsInterface"); // name -> description + rawText
  const tsFunctions = await db.get("TsFunction"); // name -> description + rawText
  const operations = await db.get("Operation"); // name -> description
  const bundleConfigs = await db.get("BundleConfig"); // slugify(name) -> description / gitRepoUrl

  const modelItemsArrays = [
    tsVariables.map((x) => addModelName(x, "TsVariable")),
    tsInterfaces.map((x) => addModelName(x, "TsInterface")),
    tsFunctions.map((x) => addModelName(x, "TsFunction")),
    operations.map((x) => addModelName(x, "Operation")),
    bundleConfigs.map((x) => addModelName(x, "BundleConfig")),
  ];

  // duplicates will overwrite each other, so let's do the most important ones last
  const mappedObject = mergeObjectsArray(
    modelItemsArrays.flat().map((item) => {
      return {
        [item.name]: getSubsetFromObject(item as AugmentedAnyModelType, [
          "name",
          "slug",
          "id",
          "description",
          "operationName",
          "rawText",
          "gitRepoUrl",
        ]),
      };
    })
  );

