checkQueue.rawText:
 async () => {
  const isOffline = !(await isOnline());
  const isBusy = await isSystemBusy();

  const startTime = Date.now();

  const allQueueItems = (await db.get("Queue")).sort((a, b) =>
    a.priority === "high" ? 1 : -1
  );

  /**
   * Light queue items we can do asap
   */
  const regularQueueItems = allQueueItems
    .filter((x) => {
      const fn = sdk[x.functionName as keyof typeof sdk] as
        | ProjectWatcher
        | undefined;

      if (isOffline && fn?.config?.isInternetRequired) {
        return false;
      }
      if (fn?.config?.isHeavy || fn?.config?.isBrowserRequired) {
        return false;
      }
      return true;
    })
    .slice(0, MAX_REGULAR_ITEMS_AMOUNT);

  /**
   * Only get heavy items if system isn't busy
   */
  const heavyItems = isBusy
    ? []
    : allQueueItems.filter((x) => {
        const fn = sdk[x.functionName as keyof typeof sdk] as
          | ProjectWatcher
          | undefined;

        return (
          fn?.config?.isHeavy && (!isOffline || !fn?.config?.isBrowserRequired)
        );
      });

  const idlePages = (await db.get("BrowserPage")).filter((p) => p.isIdle);
  const browserQueueItems = allQueueItems.filter((x) => {
    const fn = sdk[x.functionName as keyof typeof sdk] as
      | ProjectWatcher
      | undefined;

    return (
      fn?.config?.isBrowserRequired &&
      (!isOffline || !fn?.config?.isBrowserRequired)
    );
  });
  const browserRunnableNowAmount = 1; //idlePages.length
  const browserItemsToExecute = browserQueueItems.slice(
    0,
    browserRunnableNowAmount
  );

  const queueItemsToExecute = [
    ...heavyItems,
    ...browserItemsToExecute,
    ...regularQueueItems,
  ].filter(notEmpty);

  if (queueItemsToExecute.length === 0) {
    console.log("Checking queue: No queue", {
      isBusy,
      isOffline,
      allQueueItems: allQueueItems.length,
    });
    return;
  }

  const lateQueueItemsToNotify = allQueueItems.filter(
    (x) =>
      x.notifyLateAfterSeconds &&
      Date.now() - x.createdAt > x.notifyLateAfterSeconds * 1000 &&
      !x.hasNotifiedLate
  );

  // 1. check how many chrome tabs are available (according to OS settings / memory limit)

  if (browserRunnableNowAmount < lateQueueItemsToNotify.length) {
    //email the ones that won't be executed
    console.log(
      {
        browserRunnableNowAmount,
        lateQueueItemsToNotify: lateQueueItemsToNotify.length,
      },
      "SHOULD EMAIL SOME THAT CAN'T BE EXECUTED AND PUT THEM ON NOTIFIED WITH ETA"
    );
  }

  // 2. check how many queue items can be executed now, and sort by importancy
  // NB: limit to 1 for now to test if that works

  console.log(`Executing ${queueItemsToExecute.length} from queue, one by one`);

  // 3. take the ones that are most important and execute those, only for a minute!

  await oneByOne(queueItemsToExecute, async (queue) => {
    const fn = sdk[queue.functionName as keyof typeof sdk] as
      | ((...parameters: any[]) => any)
      | undefined;

    if (!fn) return;

    if (Date.now() > startTime + 60000) {
      // time's up! skip the rest, because this function starts again.
      return;
    }

    await fn(...queue.parameters);

    // after it's done, remove from queue
    const removeResult = await db.remove(
      "Queue",
      (item) => queue.id === item.id
    );
  });
}

