getContextualPromptCategories.rawText:
 async () => {
  const contextualPrompts = await db.get("ContextualPrompt");

  const stackCount = contextualPrompts.reduce((stackCount, current) => {
    // get the stack for this contextualPrompt
    const stack: string[] | undefined = current.categoryStack;

    if (!stack || stack.length === 0) {
      const already = stackCount.__root;
      return {
        ...stackCount,
        __root: already ? already + 1 : 1,
      };
    }

    const everyStackPart: string[] = stack.map((_, index, array) => {
      const stackUntilHere = array.slice(0, index + 1).join(".");
      return stackUntilHere;
    });

    const newStackCount = everyStackPart.reduce((stackCount, stackPart) => {
      const already = stackCount[stackPart];
      return {
        ...stackCount,
        [stackPart]: already ? already + 1 : 1,
      };
    }, stackCount);

    return newStackCount;
  }, {} as StackCount);

  // now, let's make a childObject out of that

  const { __root, ...stackCountWithoutRoot } = stackCount;

  const categories = Object.keys(stackCountWithoutRoot)
    .map((key) =>
      getObjectForkKeyRecursively(stackCount, key, key, contextualPrompts)
    )
    .filter(notEmpty);

  const rootCategoryChildObject: CategoryChildObject = {
    category: "root",
    categoryStack: [],
    count: __root + sum(categories.map((x) => x.count)),
    children: categories,
  };

  return rootCategoryChildObject;
}

getContextualPromptResultJsonFilePath.rawText:
 async (
  projectRelativePath?: string
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;
  const prompt_absolutePath = projectRelativePath
    ? path.join(projectRoot, projectRelativePath)
    : undefined;

  const exists = prompt_absolutePath
    ? fs.existsSync(prompt_absolutePath)
    : undefined;

  const stats =
    exists && prompt_absolutePath
      ? await fs.stat(prompt_absolutePath)
      : undefined;
  const isFolder = stats ? stats.isDirectory() : false;
  const isFile = stats ? stats.isFile() : false;
  const pathParse = prompt_absolutePath
    ? path.parse(prompt_absolutePath)
    : undefined;

  const contextualPromptResultsJsonPath =
    pathParse && isFile
      ? path.join(
          pathParse.dir,
          ".index",
          pathParse.name,
          "prompt-results.json"
        )
      : isFolder && prompt_absolutePath
      ? path.join(prompt_absolutePath, ".index", "prompt-results.json")
      : undefined;

  return contextualPromptResultsJsonPath;
}

getContextualPrompt.rawText:
 async (
  contextualPromptSlug: string | undefined,
  customPromptContent: string | undefined,
  saveNewPromptWithName: string | null,
  contextType: FileType | undefined
): Promise<(Creation<ContextualPrompt> & { slug: string }) | undefined> => {
  if (contextualPromptSlug) {
    const contextualPrompts = await db.get("ContextualPrompt");

    const contextualPrompt = contextualPrompts.find(
      (x) => x.slug === contextualPromptSlug
    );

    if (!contextualPrompt) {
      return undefined;
    }

    return contextualPrompt;
  }

  if (!customPromptContent || customPromptContent.length === 0) {
    return undefined;
  }

  const isNew = saveNewPromptWithName && saveNewPromptWithName.length > 0;

  // console.log({ saveNewPromptWithName, isNew });
  const name = isNew ? saveNewPromptWithName : generateId();
  const newContextualPrompt: Creation<ContextualPrompt> & { slug: string } = {
    name,
    slug: slugify(name),
    promptContent: customPromptContent,
    contextType: [contextType || "text"],
    model: "chat-gpt",
    usesContext: customPromptContent?.includes("%context"),
    usesSelection: customPromptContent?.includes("%selection"),
    usesAnyContext: customPromptContent?.includes("%any"),
  };

  if (isNew) {
    await db.upsert("ContextualPrompt", newContextualPrompt);
  }

  return newContextualPrompt;
}

getObjectForkKeyRecursively.rawText:
 (
  stackCount: StackCount,
  key: string,
  originalKey: string,
  items: ContextualPrompt[]
): CategoryChildObject | undefined => {
  const categoryStack = key.split(".");

  if (categoryStack.length !== 1) return;

  const firstCategory = categoryStack[0];

  //get all children:

  //1) find all keys that start with firstCategory
  const childrenKeys = Object.keys(stackCount).filter((key) =>
    key.startsWith(`${firstCategory}.`)
  );

  //2) remove the firstCategory from the keys
  const strippedKeys = childrenKeys.map((key) =>
    key.replace(`${firstCategory}.`, "")
  );

  // create strippedStackCount
  const strippedStackCount = strippedKeys.reduce((newStackCount, key) => {
    return { ...newStackCount, [key]: stackCount[`${firstCategory}.${key}`] };
  }, {} as StackCount);

  //3) insert into this same function
  const categoryChildren = strippedKeys
    .map((strippedKey, index) =>
      getObjectForkKeyRecursively(
        strippedStackCount,
        strippedKey,
        childrenKeys[index],
        items
      )
    )
    .filter(notEmpty);

  const itemChildren = items
    .filter(
      (x) =>
        x.categoryStack &&
        categoryStack[categoryStack.length - 1] ===
          x.categoryStack[x.categoryStack.length - 1]
    )
    .map((item) => {
      const { categoryStack, description, name, title, pricing } = item;
      const object: CategoryChildObject = omitUndefinedValues({
        categoryStack: categoryStack!,
        count: 1,
        description,
        name,
        title,
        pricing,
      });
      return object;
    });

  //  console.log({ itemChildren, categoryStack });

  const children = categoryChildren.concat(itemChildren);

  const object: CategoryChildObject = {
    category: firstCategory,
    categoryStack,
    count: stackCount[firstCategory],
  };

  if (children.length) object.children = children;

  return object;
}

