forAllFolders.rawText:
 async ({
  type,
  callback,
  basePath,
  onlyRoot,
  command,
  shell,
  fileName,
  folderName,
  ignore,
}: AllInputType): Promise<void> => {
  let locations =
    type === "git"
      ? await exploreGitRepoFolders({ basePath })
      : type === "operations"
      ? await exploreOperationFolders({ basePath })
      : type === "folder" && folderName
      ? (
          await explore({
            basePath,
            exact: true,
            doNotExploreChildFolders: onlyRoot,
            searchLevel: "folder",
            doNotExploreMatch: true,
            // should be able to ignore stuff
            ignore,
            search: folderName,
          })
        ).map((x) => x.path)
      : type === "file"
      ? (
          await explore({
            basePath,
            exact: true,
            searchLevel: "fileName",
            search: fileName,
          })
        ).map((x) => x.path)
      : [];

  if (locations.length === 0) {
    // console.log("Nothing found");
    return;
  }

  // console.log({ locations: locations.length });

  if (fileName && type !== "file") {
    locations = locations.map((f) => path.join(f, fileName));
  }

  // console.log({ locations });

  await oneByOne(locations, async (location, index) => {
    // console.log({ location });
    if (command) {
      execSync(command, {
        env: {
          LOCATION: location,
        },
        cwd: getFolder(location),
        encoding: "utf8",
        stdio: "inherit",
        // shell,
      });
    }

    if (callback) {
      await callback(location, index);
    }
  });

  // console.log("done!");
}

