removeAll.rawText:
 async ({
  basePath,
  fileName,
  folderName,
  type,
  shell,
  ignore,
  onlyRoot,
}: Omit<AllInputType, "command">) => {
  const callback = (folderName: string) =>
    new Promise<void>(async (resolve) => {
      try {
        if (fs.existsSync(folderName)) {
          await fs.rm(folderName, { recursive: true });
        }
        resolve();
      } catch (e) {
        console.log(
          "Shouldnt happen, but we got a catch at removing an existing folder"
        );
        resolve();
      }
    });

  return forAllFolders({
    basePath,
    fileName,
    onlyRoot,
    folderName,
    type,
    callback,
    shell,
    ignore,
  });
}

renameAll.rawText:
 async ({
  filePaths,
  newFileName,
  newFilePath,
}: {
  filePaths: string[];
  /**
   * if given, all files will be replaces by this filename
   */
  newFileName?: string;
  /**
   * if given, this will be used to determine the new path
   */
  newFilePath?: (oldPath: string) => string;
}) => {
  return forAllFiles({
    filePaths,
    callback: (filePath) =>
      new Promise<void>(async (resolve) => {
        if (fs.existsSync(filePath)) {
          const newPath = newFileName
            ? path.join(getFolder(filePath), newFileName)
            : newFilePath?.(filePath);

          if (!newPath) {
            return log("couldn't determine new path", { type: "error" });
          }
          console.log(`renamed ${filePath} to ${newPath}`);
          await fs.rename(filePath, newPath);
        }
        resolve();
      }),
  });
}

