untypedApiFunction.rawText:
 async (
  fnName: string,
  config: ApiConfig,
  ...parameters: any
): Promise<RealApiReturnType<any>> => {
  // TODO: document this convention
  const storageAuthToken: string | undefined = await getItem(
    AUTH_TOKEN_STORAGE_KEY
  );

  const authToken = config.authToken || storageAuthToken;
  const realApiUrl = config.apiUrl || apiUrl;

  if (realApiUrl === undefined) {
    return {
      isSuccessful: false,
      isNotConnected: true,
      message: "The API cannot be found",
    };
  }

  const fullUrl = `${realApiUrl}/function/${fnName}`;

  try {
    const abortController = new AbortController();
    const id = setTimeout(
      () => abortController.abort(),
      config.timeout || 10000
    );

    const response = await fetch(fullUrl, {
      method: "POST",
      signal: config.timeout ? abortController.signal : undefined,
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ authToken, parameters }),
    })
      .then((response) => response.json())
      .then((response) => {
        setItem(API_NO_RESPONSE_TIME_KEY, "0");

        return response;
      })
      .catch((error) => {
        console.log({
          explanation:
            "Your request could not be executed, you may be disconnected or the server may not be available.",
          error,
          errorStatus: error.status,
          errorString: String(error),
        });

        setItem(API_NO_RESPONSE_TIME_KEY, String(Date.now()));
        return {
          isSuccessful: false,
          isNotConnected: true,
          message:
            "Error! There's no API running (yet). Please read the docs to set this up locally. More info in the console.",
        };
      });

    clearTimeout(id);
    return response;
  } catch (e) {
    setItem(API_NO_RESPONSE_TIME_KEY, String(Date.now()));

    return {
      isSuccessful: false,
      isNotConnected: true,
      message:
        "The API didn't resolve, and the fetch crashed because of it: " +
        String(e),
    };
  }
}

AppsMenu.rawText:
 () => {
  const [loading, setLoading] = useState<{ [appName: string]: boolean }>({});
  const appsQuery = queries.useGetAllAppOperations();
  const alert = useAlert();
  return (
    <Div className="flex flex-row flex-wrap justify-center gap gap-2">
      {appsQuery.data?.result
        ?.filter((x) => x.name !== "function-server")
        .map((appOperation) => {
          return (
            <Div
              key={appOperation.name}
              className="z-50 flex flex-col items-center cursor-pointer"
              onClick={async () => {
                const goToSite = () => {
                  const url = `http://localhost:${appOperation.port}`;
                  window.open(url, "_blank");
                };

                if (!appOperation.isOnline) {
                  setLoading({ ...loading, [appOperation.name]: true });
                  const { result, isSuccessful, message } = await api.startApp(
                    appOperation.name,
                    true
                  );

                  if (!isSuccessful || !result?.isSuccessful) {
                    alert?.(
                      `Error`,
                      result?.error?.message || message || "No message"
                    );
                    setLoading({ ...loading, [appOperation.name]: false });
                    return;
                  }

                  // TODO: apparently we need to wait a second before the next.js project is really start up... we can easily improve this by watching the stdout on the server and look for a message that says "it's running on http://localhost:PORT"
                  setTimeout(() => {
                    goToSite();
                    appsQuery.refetch();
                    setLoading({ ...loading, [appOperation.name]: false });
                  }, 1000);

                  return;
                }
                // if the app is already turned on, don't load, don't open api, just go there...
                goToSite();
              }}
            >
              <Tooltip
                placement="top"
                tooltip={
                  <MarkdownContent
                    content={
                      appOperation.description ||
                      appOperation.name ||
                      "No description set"
                    }
                    config={{}}
                  />
                }
              >
                <Div className="bg-white border border-black mb-2 rounded-lg w-12 h-12 flex items-center justify-center hover:scale-150 hover:mt-3 hover:-mb-3 transition-all">
                  {loading[appOperation.name] === true ? (
                    <FancyLoader medium />
                  ) : (
                    <P title={appOperation.name} className="text-4xl">
                      {appOperation.emoji ||
                        appOperation.name.charAt(0).toLocaleUpperCase()}
                    </P>
                  )}
                </Div>
              </Tooltip>

              {appOperation.isOnline ? (
                <Div className="w-2 h-2 rounded-full bg-gray-700 dark:bg-gray-200" />
              ) : (
                <Div className="w-2 h-2" />
              )}
            </Div>
          );
        })}
    </Div>
  );
}

