findAssetParametersRecursively.rawText:
 (
  object: {
    [key: string]: any;
  },
  stack?: string[]
): AssetParameter[] => {
  if (!object) return [];

  const result = Object.keys(object)
    .map((objectProperty) => {
      const value = object[objectProperty];

      const assetInputType = getAssetInputType(objectProperty);

      if (assetInputType) {
        return [
          {
            assetInputType,
            parameterName: objectProperty,
            stack,
          },
        ];
      }

      if (typeof value === "object") {
        const newStack = stack ? [...stack, objectProperty] : [objectProperty];

        return findAssetParametersRecursively(value, newStack);
      }
    })
    .filter(notEmpty)
    .flat();

  return result;
}

getConversionInfoFromType.rawText:
 (
  uploadMimeType: string | undefined
): {
  uploadMimeType: string | undefined;
  targetFormat: string | undefined;
  isUnchecked?: boolean;
} => {
  const parts = uploadMimeType?.split("/") || [];
  const firstPart = parts.length > 0 ? parts[0] : undefined;
  const lastPart = parts.length > 0 ? parts[parts.length - 1] : undefined;

  console.log({ uploadMimeType, lastPart });
  if (uploadMimeType === "audio/mp4") {
    return { uploadMimeType, targetFormat: "mp3" };
  }

  if (lastPart === "wav") {
    return { uploadMimeType, targetFormat: "mp3" };
  }

  if (lastPart === "x-m4a") {
    return { uploadMimeType, targetFormat: "mp3" };
  }

  if (firstPart === "audio") {
    return { uploadMimeType, targetFormat: "mp3" };
  }

  if (lastPart === "quicktime") {
    return { uploadMimeType, targetFormat: "mp4" };
  }

  if (firstPart === "video") {
    return { uploadMimeType, targetFormat: "mp4" };
  }

  if (firstPart === "image") {
    return { uploadMimeType, targetFormat: "jpg" };
  }

  return { uploadMimeType, targetFormat: lastPart, isUnchecked: true };
}

