findAbsoluteAssetPathFromReference.rawText:
 async (
  projectRelativeReferencingFilePath: string,
  referencingFileRelativeAssetPath: string
): Promise<{
  isSuccessful: boolean;
  message: string;
  absoluteAssetPath?: string;
}> => {
  if (
    !projectRelativeReferencingFilePath ||
    !referencingFileRelativeAssetPath
  ) {
    console.log({
      projectRelativeReferencingFilePath,
      referencingFileRelativeAssetPath,
    });
    return {
      isSuccessful: false,
      message:
        "Incorrect parameters for fetching absolute asset from reference",
    };
  }
  // TODO: STEP 1: ensure the user is authenticated and can access the referencing file

  const projectRoot = getProjectRoot();
  if (!projectRoot)
    return {
      isSuccessful: false,
      message: "No project root",
    };

  const extension = getExtension(projectRelativeReferencingFilePath);
  const allowedExtensions = ["ts", "tsx", "md", "mdx", "json"];
  if (!allowedExtensions.includes(extension)) {
    return {
      isSuccessful: false,
      message: "Invalid extension",
    };
  }

  const absoluteReferencingFilePath = path.join(
    projectRoot,
    projectRelativeReferencingFilePath
  );

  const exists = fs.existsSync(absoluteReferencingFilePath);

  if (!exists) {
    return {
      isSuccessful: false,
      message: "Invalid file",
    };
  }

  const contents = await fs.readFile(absoluteReferencingFilePath, "utf8");

  const patternType = extension === "json" ? "json" : "markdown";

  const pattern =
    patternType === "json"
      ? `"relativePath": "${referencingFileRelativeAssetPath}"`
      : `](${referencingFileRelativeAssetPath})`;

  const foundPattern = contents.includes(pattern);

  const filename = path.parse(projectRelativeReferencingFilePath).name;

  /**
   * If the name of the file is extending the name of the referencing file, it doesn't need to be referenced, as it can be assumed it's an indexation file.
   */
  const isIndexationFile = referencingFileRelativeAssetPath.startsWith(
    `./${filename}`
  );

  if (!foundPattern && !isIndexationFile) {
    // console.log({ contents, pattern });
    return {
      isSuccessful: false,
      message: "Pattern not found",
    };
  }

  const referencingFileFolderPath = path.parse(absoluteReferencingFilePath).dir;

  const absoluteAssetPath = path.join(
    referencingFileFolderPath,
    referencingFileRelativeAssetPath
  );

  if (!fs.existsSync(absoluteAssetPath)) {
    return {
      isSuccessful: false,
      message: "The referenced asset does not exist",
    };
  }

  return { isSuccessful: true, message: "Found path", absoluteAssetPath };
}

findAllProjectMedia.rawText:
 async (
  searchQuery?: string,
  /**
   * defaults to absolute
   */
  returnType?: "projectRelative" | "absolute"
): Promise<string[]> => {
  const projectRoot = getProjectRoot();

  if (!projectRoot) return [];

  const dbPath = path.join(projectRoot, databaseFolderName);

  const operationBasePaths = Object.values(operations).map(
    (projectRelativePath) => path.join(projectRoot, projectRelativePath)
  );

  const databaseFolderPaths = operationBasePaths
    .map((absoluteOperationPath) =>
      path.join(absoluteOperationPath, databaseFolderName)
    )
    .concat(dbPath)
    .filter(fs.existsSync);

  const dbResults = await explore({
    basePath: databaseFolderPaths,
    extension: mediaExtensions,
    search: searchQuery,
  });

  const operationResults = await explore({
    basePath: operationBasePaths,
    extension: mediaExtensions,
    ignore: [...generatedFolders, databaseFolderName, sourceFolderName],
    search: searchQuery,
  });

  const anywhereResults = await explore({
    cancelRecursionOn: pathArrayIsOperation,
    basePath: projectRoot,
    extension: mediaExtensions,
    ignore: [databaseFolderName, "node_modules", ".git"],
  });

  const results = [dbResults, operationResults, anywhereResults]
    .flat()
    .map((x) => x.path);

  return returnType === "projectRelative"
    ? results.map((x) => makeRelative(x, projectRoot))
    : results;
}

