getStorageLocationInfo.rawText:
 (
  absoluteReferencingFilePath: string,
  modelName?: string
): {
  type: "typescript" | "database" | "markdown";
  absoluteAssetBaseFolderPath: string;
} => {
  if (absoluteReferencingFilePath.includes(`/${sourceFolderName}/`)) {
    /**
     *In typescript, the asset can be stored in the `/assets` folder of the operation. The alt is in the markdown.
     */
    const operationBasePath = findOperationBasePath(
      absoluteReferencingFilePath
    );
    if (operationBasePath) {
      const assetsPath = path.join(operationBasePath, "assets");

      return { type: "typescript", absoluteAssetBaseFolderPath: assetsPath };
    }
  }
  if (
    modelName &&
    absoluteReferencingFilePath.includes(`/${databaseFolderName}/`)
  ) {
    const databaseFolderBasePath = absoluteReferencingFilePath
      .split(`/${databaseFolderName}/`)[0]
      .concat(`/${databaseFolderName}`);

    const databaseFolderRelativeReferenceLocationPath = makeRelative(
      absoluteReferencingFilePath,
      databaseFolderBasePath
    );
    const isStoredInFolder =
      databaseFolderRelativeReferenceLocationPath.split("/").length > 1;

    const absoluteAssetBaseFolderPath = isStoredInFolder
      ? path.parse(absoluteReferencingFilePath).dir
      : path.join(databaseFolderBasePath, `${kebabCase(modelName)}-assets`);

    /**
     * In database models, the asset can be stored in
     *
     * - in `[modelName]-assets` folder inside of `db` if the data is stored in the root of the db
     * - in the same folder as the data itself if the data is stored in folders and more separated files
     */
    return { type: "database", absoluteAssetBaseFolderPath };
  }

  /**
   * In markdown, the asset can be stored in the same folder as the markdown file. The alt is in the markdown. The filename can be something else, should be kept original unless changed
   */
  return {
    type: "markdown",
    absoluteAssetBaseFolderPath: path.parse(absoluteReferencingFilePath).dir,
  };
}

processAsset.rawText:
 async (
  /**
   * The backendAsset that may need processing
   */
  backendAsset?: BackendAsset | BackendAsset[]
): Promise<undefined | BackendAsset> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("processAsset: No project root");
    return;
  }
  const realBackendASset = backendAsset ? takeFirst(backendAsset) : undefined;

  if (!realBackendASset) {
    console.log("processAsset: No real backend asset");

    return;
  }

  const {
    alt,
    name,
    relativePath,
    temporaryDestination,
    projectRelativeReferencingFilePath,
    modelName,
  } = realBackendASset;

  if (!projectRelativeReferencingFilePath) {
    log(
      `processAsset: projectRelativeReferencingFilePath wasn't provided, not processing asset`,
      { type: "warning" }
    );
    return undefined;
  }

  const absoluteReferencingFilePath = path.join(
    projectRoot,
    projectRelativeReferencingFilePath
  );

  // console.log("processing asset", { backendAsset });
  const slugifiedName = slugify(name && name.length > 0 ? name : "untitled");

  if (!relativePath && !temporaryDestination) {
    log("processAsset: no relativePath, no temporaryDestination", {
      type: "warning",
    });
    // NB: we must have either a relativePath or a temporaryDestination, otherwise this is an invalid input
    return undefined;
  }

  const absoluteTemporaryDestination = temporaryDestination
    ? path.join(getTemporaryAssetsFolderPath(), temporaryDestination)
    : undefined;

  // NB: If a `temporaryDestination` is given, if it doesn't exist, we return nothing, this is an invalid input.
  if (absoluteTemporaryDestination) {
    if (!fs.existsSync(absoluteTemporaryDestination)) {
      log("processAsset: absoluteTemporaryDestination does not exist", {
        type: "warning",
      });

      return undefined;
    }
  }

  const oldAssetStoragePath = relativePath
    ? path.join(path.parse(absoluteReferencingFilePath).dir, relativePath)
    : undefined;

  // NB: if a relativePath is provided without a temporaryDestination, it means the file should already be there. If it's not there, we return nothing, invalid input.
  if (oldAssetStoragePath && !temporaryDestination) {
    if (!fs.existsSync(oldAssetStoragePath)) {
      log(
        `processAsset: oldAssetStoragePath does not exist (${oldAssetStoragePath})`,
        {
          type: "warning",
        }
      );

      return undefined;
    }
  }

  // Now we can assume the extension of the temporarydestination is correct, because it gets put there from upload
  const extension = temporaryDestination
    ? temporaryDestination.split(".").pop()
    : relativePath?.split(".").pop();

  if (!extension) {
    log("processAsset: could not create extension", { type: "warning" });

    // NB: extension must exist now.
    return undefined;
  }

  /*
   */

  const storageLocationInfo = getStorageLocationInfo(
    absoluteReferencingFilePath,
    modelName
  );

  const finalAssetStoragePath = path.join(
    storageLocationInfo.absoluteAssetBaseFolderPath,
    `${slugifiedName}.${extension}`
  );

  let finalAvailableStoragePath: string = finalAssetStoragePath;

  if (absoluteTemporaryDestination) {
    finalAvailableStoragePath = getFirstAvailableFilename(
      finalAssetStoragePath
    );

    await renameAndCreate(
      absoluteTemporaryDestination,
      finalAvailableStoragePath
    );
  } else if (oldAssetStoragePath) {
    if (oldAssetStoragePath !== finalAssetStoragePath) {
      finalAvailableStoragePath = getFirstAvailableFilename(
        finalAssetStoragePath
      );

      // the name has changed
      await renameAndCreate(oldAssetStoragePath, finalAvailableStoragePath);
    }
  }

  const newRelativePath = getRelativeLinkPath(
    absoluteReferencingFilePath,
    finalAvailableStoragePath
  );

  //({ newRelativePath });

  console.log({ alt, relativePath });
  return { alt, relativePath: newRelativePath };
}

processItemAssets.rawText:
 async <KInterfaceName extends keyof DbModels>(
  item: AugmentedAnyModelType,
  interfaceName: KInterfaceName,
  operationName: string | null,
  customQueryConfig: CustomQueryConfig
): Promise<AugmentedAnyModelType | undefined> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;
  const absoluteReferencingFilePath = await db.getDbFileLocationPath(
    item,
    operationName,
    interfaceName,
    customQueryConfig
  );
  if (!absoluteReferencingFilePath) return item;

  const projectRelativeReferencingFilePath = makeRelative(
    absoluteReferencingFilePath,
    projectRoot
  );

  const assetParameters = findAssetParametersRecursively(item);

  const assetsToPutOnObject = await Promise.all(
    assetParameters.map(async (assetParameter) => {
      const location = [
        ...(assetParameter.stack || []),
        assetParameter.parameterName,
      ];

      const value = getParameterAtLocation<
        BackendAsset | BackendAsset[] | undefined
      >(item, location);

      const backendAssetToStore = Array.isArray(value)
        ? (
            await Promise.all(
              value.map((asset) =>
                processAsset({
                  ...asset,
                  projectRelativeReferencingFilePath,
                  modelName: interfaceName,
                })
              )
            )
          ).filter(notEmpty)
        : value
        ? await processAsset({
            ...value,
            projectRelativeReferencingFilePath,
            modelName: interfaceName,
          })
        : undefined;

      return { backendAssetToStore, location };
    })
  );

  assetsToPutOnObject.forEach((assetWithLocation) => {
    setKeyAtLocation(
      assetWithLocation.location.join("."),
      assetWithLocation.backendAssetToStore,
      item
    );
  });

  // console.dir(
  //   { assetParameters, assetsToPutOnObject, finalItem: item },
  //   { depth: 999 }
  // );

  return item;
}

removeOldTemporaryAssets.rawText:
 async (): Promise<{
  removedAmount: number;
}> => {
  const temporaryAssetsFolderPath = getTemporaryAssetsFolderPath();
  const contents = await fs.readdir(temporaryAssetsFolderPath, {
    encoding: "utf8",
  });

  let removedAmount = 0;
  await Promise.all(
    contents.map(async (fileOrFolderName) => {
      const absolutePath = path.join(
        temporaryAssetsFolderPath,
        fileOrFolderName
      );

      const statistics = await fs.stat(absolutePath);

      if (Date.now() - statistics.ctimeMs > 86400 * 1000) {
        removedAmount++;
        // more than a day old
        await fs.rm(absolutePath, { recursive: true });
      }
    })
  );

  return { removedAmount };
}

