getSrc.rawText:
 (
  asset: Asset,
  projectRelativeReferencingFilePath: string,
  isNextStaticProductionBuild?: boolean
) => {
  // console.log({ apiUrl });
  /**
   * NB: `relativePath` is required
   */
  const getRemoteUrl = (isDownload: boolean) =>
    getReferencedAssetApiUrl(
      apiUrl,
      projectRelativeReferencingFilePath,
      asset.relativePath!,
      isDownload
    );

  const getPublicUrl = (relativePath: string) =>
    `markdown-assets/${path.join(
      path.parse(projectRelativeReferencingFilePath).dir,
      relativePath
    )}`;
  /**
   * NB: `asset.temporaryDestination` is not a URL that can be used to retreive the image as "src"
   */
  const src = asset.absoluteUrl
    ? asset.absoluteUrl
    : asset.blobPath
    ? asset.blobPath
    : isNextStaticProductionBuild && asset.relativePath
    ? getPublicUrl(asset.relativePath)
    : getRemoteUrl(false);

  // console.log("UseAssetSRC:", src);
  const downloadRemotely = !asset.blobPath && !!asset.relativePath;

  const downloadUrl =
    isNextStaticProductionBuild && asset.relativePath
      ? getPublicUrl(asset.relativePath)
      : downloadRemotely
      ? getRemoteUrl(true)
      : // NB: other type is ugly when downloading. Also doesn't make sense much to want to download an asset after uploading
      asset.type !== "other"
      ? asset.blobPath
      : undefined;

  return { src, downloadUrl };
}

InteractiveAsset.rawText:
 (props: {
  asset: Asset;
  /**
   * See `AssetInput` for more info
   */
  attachTokenToFilename?: boolean;
  projectRelativeReferencingFilePath: string;
  remove: () => void;
  onChange: (newAsset: Asset) => void;
}) => {
  const {
    asset,
    remove,
    projectRelativeReferencingFilePath,
    onChange,
    attachTokenToFilename,
  } = props;

  /**
   *
   * Name that includes token
   */
  const nameWithToken =
    asset.name && asset.name.length > 0
      ? // You've edited the name, and this it's stored on the frontend `Asset`
        asset.name
      : asset.relativePath
      ? // The asset comes from the backend, the name can be deducted from the `relativePath` by removing the token first, then adding it
        getNameWithTokenFromRelativePath(
          asset.relativePath,
          attachTokenToFilename
        )
      : // There is no backend name nor there's a name set by the user. NB: this should not really happen as it must be present on Asset after recording/selecting an asset...
        "";

  const { nameWithoutToken, token: previousToken } = removeTokenIfPresent(
    nameWithToken,
    attachTokenToFilename
  );

  const uploadProgressText =
    asset.uploadProgress === undefined || asset.uploadProgress === 0
      ? ""
      : asset.uploadProgress === 1
      ? "‚úÖ"
      : `${Math.round(asset.uploadProgress * 100)}%`;

  return (
    <Div className="relative border border-black rounded-xl p-2 m-2">
      <Span className="absolute right-1 top-[-7px]">
        <ClickableIcon emoji="‚®Ø" onClick={() => remove()} />
      </Span>
      <AssetView
        projectRelativeReferencingFilePath={projectRelativeReferencingFilePath}
        asset={asset}
      />

      <Div className="flex flex-row">
        <Input
          placeholder="Name"
          value={nameWithoutToken}
          style={{ backgroundColor: "transparent", border: "1px solid black" }}
          onChange={(e) => {
            const newName = addToken(
              e.target.value,
              previousToken,
              attachTokenToFilename
            );

            console.log({ newName });

            const newAsset = { ...asset, name: newName };
            onChange(newAsset);
          }}
        />
        <Input
          placeholder="Alternate text"
          style={{ backgroundColor: "transparent", border: "1px solid black" }}
          value={asset.alt || ""}
          onChange={(e) => {
            const newAsset = { ...asset, alt: e.target.value };
            onChange(newAsset);
          }}
        />
        {uploadProgressText}
      </Div>
    </Div>
  );
}

