useAssetInfo.rawText:
 (url?: string, filename?: string) => {
  if (!url && !filename) {
    console.log("MUST PROVIDE EITHER URL OR FILENAME");
    return;
  }
  const urlOrFilename = (url || filename)!;
  const realFilename = (url ? url.split("/").pop() : filename)!;
  const isTextFile = isText(realFilename) === true ? true : false;

  const [rawText, setRawText] = useState<string | null>(null);
  useEffect(() => {
    if (!!url && isTextFile) {
      fetch(url).then((result) => {
        result.text().then((text) => setRawText(text));
      });
    }
  }, [url, isTextFile]);

  const type = getTypeFromUrlOrPath(urlOrFilename);

  return { rawText, type };
}

useAsset.rawText:
 (
  asset: Asset | undefined,
  projectRelativeReferencingFilePath?: string | null,
  isNextStaticProductionBuild?: boolean
) => {
  if (!asset || !projectRelativeReferencingFilePath) return;

  const { src, downloadUrl } = getSrc(
    asset,
    projectRelativeReferencingFilePath,
    isNextStaticProductionBuild
  );

  const extension = getExtensionFromAsset(asset);

  const assetInfo = useAssetInfo(src, asset.originalFilename);

  const rawText = assetInfo?.rawText || null;
  const type = asset.type || assetInfo?.type;

  return { rawText, type, downloadUrl, src, extension };
}

