getAugmentedWords.rawText:
 async (
  manualProjectRoot?: string
): Promise<AugmentedWord[]> => {
  const projectRoot = manualProjectRoot || getProjectRoot();

  if (!projectRoot) return [];

  const operations = await db.get("Operation");

  const operationAugmentedWords: AugmentedWord[] = operations
    .map((item) => {
      const itemLocationPath = path.join(projectRoot, item.projectRelativePath);
      const operationPath = findOperationBasePath(itemLocationPath);
      if (!operationPath) return;

      const description = item.operation?.markdown || item.description;

      const augmentedWord: AugmentedWord = {
        queryPath: makeRelative(operationPath, projectRoot),
        spoiler: description,
        type: "operation",
        word: item.name,
        // NB: OperationConfig is located in operation basePath
        projectRelativeMarkdownSourcePath: path.join(
          operationPath,
          "OPERATION.md"
        ),
      };

      return augmentedWord;
    })
    .filter(notEmpty);

  const tsVariables = await db.get("TsVariable", {
    manualProjectRoot: projectRoot,
  });

  const tsFunctions = await db.get("TsFunction", {
    manualProjectRoot: projectRoot,
  });
  const tsInterfaces = await db.get("TsInterface", {
    manualProjectRoot: projectRoot,
  });

  const interfaceAugmentedWords: AugmentedWord[] = tsInterfaces
    .map((item) => {
      // NB: TODO: figure out of this is always correct, this path must exist!
      const itemLocationPath = path.join(projectRoot, item.projectRelativePath);
      const operationPath = findOperationBasePath(itemLocationPath);
      if (!operationPath) return;

      const projectRelativeOperationPath = makeRelative(
        operationPath,
        projectRoot
      );

      const augmentedWord: AugmentedWord = {
        queryPath: `${projectRelativeOperationPath}#${getImplicitId(
          item.name
        )}`,
        spoiler: item.description,
        type: "type",
        word: item.name,
        projectRelativeMarkdownSourcePath: item.projectRelativePath,
      };

      return augmentedWord;
    })
    .filter(notEmpty);

  const functionAugmentedWords: AugmentedWord[] = tsFunctions
    .map((item) => {
      // NB: TODO: figure out of this is always correct, this path must exist!
      const itemLocationPath = path.join(projectRoot, item.projectRelativePath);
      const operationPath = findOperationBasePath(itemLocationPath);
      if (!operationPath) return;

      const projectRelativeOperationPath = makeRelative(
        operationPath,
        projectRoot
      );

      const augmentedWord: AugmentedWord = {
        queryPath: `${projectRelativeOperationPath}#${getImplicitId(
          item.name
        )}`,
        spoiler: item.description,
        type: "function",
        word: item.name,
        projectRelativeMarkdownSourcePath: item.projectRelativePath,
      };

      return augmentedWord;
    })
    .filter(notEmpty);

  const variableAugmentedWords: AugmentedWord[] = tsVariables
    .map((item) => {
      // NB: TODO: figure out of this is always correct, this path must exist!
      const itemLocationPath = path.join(projectRoot, item.projectRelativePath);
      const operationPath = findOperationBasePath(itemLocationPath);
      if (!operationPath) return;

      const projectRelativeOperationPath = makeRelative(
        operationPath,
        projectRoot
      );

      const augmentedWord: AugmentedWord = {
        queryPath: `${projectRelativeOperationPath}#${getImplicitId(
          item.name
        )}`,
        spoiler: item.description,
        type: "variable",
        word: item.name,
        projectRelativeMarkdownSourcePath: item.projectRelativePath,
      };

      return augmentedWord;
    })
    .filter(notEmpty);

  const bundleAugmentedWords: AugmentedWord[] | undefined = isBundle(
    projectRoot
  )
    ? undefined
    : await getBundleAugmentedWords();

  const allAugmentedWords: AugmentedWord[] = [
    bundleAugmentedWords,
    operationAugmentedWords,
    variableAugmentedWords,
    functionAugmentedWords,
    interfaceAugmentedWords,
  ]
    .filter(notEmpty)
    .flat();

  return allAugmentedWords;
}

getBundleAugmentedWords.rawText:
 async (): Promise<AugmentedWord[]> => {
  // NB: we need to cast the string because in OS-projects this might otherwise fail
  const bundles: AugmentedAnyModelType[] = await db.get(
    "BundleConfig" as keyof DbModels
  );

  const bundleAugmentedWords: AugmentedWord[] = bundles.map((bundle) => {
    const augmentedWord: AugmentedWord = {
      queryPath: bundle.docsRelativeFolderPath,
      spoiler: bundle.description,
      type: "bundle",
      word: bundle.slug,
      projectRelativeMarkdownSourcePath: bundle.projectRelativePath,
    };

    return augmentedWord;
