getBundleSummary.rawText:
 (bundleConfig: BundleConfig): BundleSummary => {
  const operationsPath = getRootPath("operations");
  const bundlesPath = operationsPath
    ? path.join(operationsPath, "niches")
    : undefined;

  const foldersFromRepo = bundlesPath
    ? bundleConfig.createBundleConfig.foldersFromRepo?.map((x) =>
        path.join(bundlesPath, kebabCase(bundleConfig.name), x)
      )
    : undefined;

  // const filesAndFoldersToInclude = [
  //   // ...(foldersFromRepo || []),
  //   bundleConfig.readmeRelativeFilePath,
  //   bundleConfig.docsRelativeFolderPath,
  // ].filter(notEmpty);

  const appNames = bundleConfig.createBundleConfig.bundles
    .map((x) => {
      const frontendOperationNames = makeArray(
        x.appShellOperationNames,
        x.serverOperationName
      );

      return frontendOperationNames;
    })
    .flat()
    .filter(onlyUnique2());

  const uiOperationNames = makeArray(
    bundleConfig.createBundleConfig.bundles
      .map((x) => x.uiOperationName)
      .filter(notEmpty)
  );

  const bundlePublicOperationNames = bundleConfig.createBundleConfig.bundles
    .map(
      (x) =>
        x.operations
          ?.filter((x) => x.privacy === "public")
          .map((x) => x.operationName) || []
    )
    .flat();
  const bundlePrivateOperationNames = bundleConfig.createBundleConfig.bundles
    .map(
      (x) =>
        x.operations
          ?.filter((x) => x.privacy === "private")
          .map((x) => x.operationName) || []
    )
    .flat();

  const packageNames =
    bundleConfig.createBundleConfig.dependencies
      ?.filter((x) => x.privacy === "public")
      .map((x) => x.operationName)
      .concat(uiOperationNames)
      .concat(bundlePublicOperationNames)
      .filter(onlyUnique2()) || [];

  const moduleNames =
    bundleConfig.createBundleConfig.dependencies
      ?.filter((x) => x.privacy === "private")
      .map((x) => x.operationName)
      .concat(bundlePrivateOperationNames)
      .filter(onlyUnique2()) || [];

  const summary: BundleSummary = {
    // filesAndFoldersToInclude,
    slug: bundleConfig.slug,
    packageNames,
    moduleNames,
    appNames,
  };

  // console.dir({ summary, bundleConfig }, { depth: 999 });

  return summary;
}

getDbModelsForBundle.rawText:
 async (
  bundleConfig: BundleConfig
): Promise<TsInterface[]> => {
  const bundleSummary = getBundleSummary(bundleConfig);

  const dbModels = getDbModelsFromOperations(
    bundleSummary.packageNames
      .concat(bundleSummary.moduleNames)
      .concat(bundleSummary.appNames)
  );
  return dbModels;
}

