cleanupTsDatabase.rawText:
 async (
  operationName: string,
  manualProjectRoot?: string
) => {
  //console.log("let's cleanup operation ", operationName, manualProjectRoot);
  const operationBasePath = await getOperationPath(operationName);

  //console.log({ operationBasePath });
  if (!operationBasePath) return;
  const operationRelativePaths = (
    await getPackageSourcePaths({ operationBasePath })
  ).map((absolutePath) =>
    getOperationRelativePath(absolutePath, operationBasePath)
  );

  // all typescript index models related to a ts file should be cleaned
  const removeResults = await Promise.all(
    typescriptIndexModels.map((modelName) => {
      return db.remove(
        modelName,
        (model) => {
          const shouldRemove = shouldDeleteTsModel(
            model,
            operationName,
            operationRelativePaths
          );
          /*
          if (shouldRemove) {
            console.log({
              modelName,
              operationRelativePaths,
              model: model.name,
              shouldRemove,
              ts: model.operationRelativeTypescriptFilePath,
            });
          }*/

          return shouldRemove;
        },
        { operationName, manualProjectRoot }
      );
    })
  );

  //console.log({ removeResults });

  const totalRemoved = removeResults.reduce(
    (previous, current) => previous + (current.amountRemoved || 0),
    0
  );

  return {
    amountRemoved: totalRemoved,
  };
}

shouldDeleteTsModel.rawText:
 (
  tsModel:
    | TsFunction
    | TsComment
    | TsInterface
    | TsBuildError
    | TsLintWarning
    | TsExport
    | TsImport
    | TsVariable,
  operationName: string,
  operationRelativePaths: string[]
) => {
  if (!tsModel.operationName) {
    // console.log("no operationaName");
    return true;
  }
  if (tsModel.operationName !== operationName) {
    // console.log("wrong operationName");
    return true;
  }
  if (!tsModel.operationRelativePath) {
    //   console.log("no operationRelativePath");
    return true;
  }
  if (
    !operationRelativePaths.includes(
      tsModel.operationRelativeTypescriptFilePath
    )
  ) {
    // console.log("no correct operationRelativeTypescriptFilePath");
    return true;
  }

  return false;
}

