stripComment.rawText:
 (rawCommentString: string): string => {
  const lines = rawCommentString.split("\n");
  const strippedComment = lines
    .map((line) => {
      return apply(
        [
          trim,
          stripSlashes,
          stripCommentStart,
          stripCommentEnd,
          stripStar,
          trim,
        ],
        line
      );
    })
    .join("\n");

  return apply([trimSurroundingNewlines], strippedComment);
}

trimSurroundingNewlines.rawText:
 (string: string): string => {
  return string.startsWith("\n")
    ? trimSurroundingNewlines(string.substring(1))
    : string.endsWith("\n")
    ? trimSurroundingNewlines(string.substring(0, string.length - 1))
    : string;
}

getCompileErrors.rawText:
 async (
  operationBasePath: string,
  onlyDependants?: boolean,
  manualProjectRoot?: string
): Promise<Creation<TsBuildError>[]> => {
  const moduleName = await getOperationPackageName(operationBasePath);

  if (onlyDependants) {
    log("Getting compilation errors for all dependants", { type: "important" });

    // should get all operations that have this module in their imports
    // in the beginning, no indexes for this will exist, so that's fine...
    const allImports = await db.get("TsImport", { manualProjectRoot });
    const dependants: string[] = allImports
      .filter((imp) => imp.module === moduleName)
      .map((imp) => imp?.operationName)
      .filter(notEmpty)
      .filter(onlyUnique);

    log(`Dependants ${dependants.join(", ")} (now skipping)`, {
      type: "important",
    });
    // const stuff = (
    //   await Promise.all(
    //     dependants.map(async (dep) => {
    //       const operationFolderPath = await getOperationPath(dep);
    //       if (!operationFolderPath) return null;

    //       const filePaths: string[] = await getPackageSourcePaths({
    //         packageFolder: operationFolderPath,
    //       });
    //       const tsConfig = await getTsConfig(operationFolderPath);
    //       const packageJson = await getPackageJson({ operationFolderPath });

    //       if (!tsConfig) return null;
    //       if (!packageJson) return null;

    //       return {
    //         basePath: operationFolderPath,
    //         packageJson,
    //         compilerOptions: tsConfig.compilerOptions,
    //         filePaths,
    //       };
    //     })
    //   )
    // ).filter(notEmpty);

    const allBuildErrors = [].map(getTypescriptErrorsFromFiles).flat();

    return allBuildErrors;
  } else {
    const tsConfig = await getTsConfig(operationBasePath);
    const compilerOptions = tsConfig?.compilerOptions;
    if (!compilerOptions) {
      log(
        `Couldn't find tsConfig compilerOptions for this operation (${operationBasePath}`,
        { type: "error" }
      );
      return [];
    }

    const filePaths = await getPackageSourcePaths({
      operationBasePath,
    });

    const packageJson = await getPackageJson({
      operationFolderPath: operationBasePath,
    });

    if (!packageJson) return [];

    const buildErrorsHere = getTypescriptErrorsFromFiles({
      packageJson,
      basePath: operationBasePath,
      filePaths,
      compilerOptions,
    });

    // const buildErrorsHere: TsBuildError[] = [];
    if (buildErrorsHere.length > 0) {
      return buildErrorsHere;
    } else if (!onlyDependants) {
      // do the same for all dependants, but only if this is not already a dependant (just 1 level of recursion)...
      return getCompileErrors(operationBasePath, true, manualProjectRoot);
    } else {
      return [];
    }
  }
}

writeBuildErrors.rawText:
 async (
  operationBasePath: string,
  operationManualProjectRoot?: string,
  typerepoManualProjectRoot?: string
) => {
  const operationName = getLastFolder(operationBasePath);
  const projectRoot = operationManualProjectRoot || getProjectRoot();
  if (!projectRoot) return;
  // NB: fix to globally alter real fs in order to fix EMFile error that happens in TSMorph (see https://github.com/isaacs/node-graceful-fs)
  gracefulFs.gracefulify(realFs);

  // NB: this does it for all files in the whole operation!
  const buildErrors = await getCompileErrors(
    operationBasePath,
    false,
    operationManualProjectRoot
  );

  await db.clear("TsBuildError", {
    operationName,
    manualProjectRoot: operationManualProjectRoot,
  });
  // @ts-ignore
  await db.upsert("TsBuildError", buildErrors, {
    operationName,
    manualProjectRoot: operationManualProjectRoot,
  });

  // log(`Wrote ts-build-errors for ${operationFolder}`, { type: "success" });

  process.exit(0);
}

