useContextMenu.rawText:
 (props: {
  items: ContextMenuItem[];
  longTouchDurationMs?: number;
  /**
   * Customise the container class
   */
  className?: string;

  /**
   * Customise the contextMenuItem className
   *
   * Example:
   *
   * `select-none cursor-pointer lg:hover:bg-blue-300 py-2 px-4`
   */
  itemClassName?: string;
  /**
   * 
   * Overwrite the default item using this custom renderer:
   * 
   * Example:
   * 
   * ```tsx
  customItemRender={(
    contextMenuItem,
    index,
    onClose,
    id
  ) => (
      <P
        onClick={() => {
          contextMenuItem.onClick(id);
          onClose();
        }}
        className="select-none cursor-pointer lg:hover:bg-blue-300 py-2 px-4"
        key={`menu${index}`}
      >
        {contextMenuItem.getTitle(id)}
      </P>)}

   * ```
   */
  customItemRender?: (
    contextMenuItem: ContextMenuItem,
    index: number,
    onClose: () => any,
    id: string | undefined
  ) => JSX.Element | null;
}) => {
  const {
    items,
    longTouchDurationMs,
    className,
    customItemRender,
    itemClassName,
  } = props;
  const {
    renderContextPopper: renderContextMenu,
    openContextPopperProps: openContextMenuProps,
    onClose,
    isOpen,
  } = useContextPopper({
    longTouchDurationMs,
    renderPopper: (props) => {
      const { id } = props;
      return (
        <Div
          className={
            // className ||
            "bg-white dark:bg-gray-700 border border-black max-h-80 overflow-y-scroll"
          }
        >
          {items
            .filter((x) => (x.getIsEnabled ? x.getIsEnabled(id) : true))
            .map((contextMenuItem, index) => {
              if (customItemRender) {
                return customItemRender(contextMenuItem, index, onClose, id);
              }

              return (
                <ContextMenuItemComponent
                  id={id}
                  item={contextMenuItem}
                  key={`menuItem${index}`}
                  onClose={onClose}
                  itemClassName={itemClassName}
                />
              );
            })}
        </Div>
      );
    },
  });

  // ensure it only updates when you open the menu. this breaks the whole thing! lol.
  const result = useMemo(
    () => ({ renderContextMenu, openContextMenuProps, onClose, isOpen }),
    [isOpen]
  );

  return { renderContextMenu, openContextMenuProps, onClose, isOpen };
}

useContextPopper.rawText:
 (props: {
  renderPopper: (props: { id?: string }) => JSX.Element;
  longTouchDurationMs?: number;
}): {
  isOpen: boolean;
  /**
   * Render this function anywhere in your component. Ensure it's high enough up the tree to make sure the positioning is correct. Not tested in modals and scrollable divs...
   */
  renderContextPopper: () => JSX.Element | null;

  /**
   * Callback to close the popper from anywhere
   */
  onClose: () => void;
  /**
   * Attach these props to the element that should be right-clickable or long-pressable
   *
   * You can also pass an id to this element in order to pass this to the callback
   * e.g.
   *
   * ```tsx
   * <div id="some-id" {...openContextPopperProps}>your html</div>
   * ```
   *
   * If you also want to attach your own properties, you can destructure this and wrap the individual props in your final prop.
   */
  openContextPopperProps: {
    ref: React.RefObject<HTMLDivElement>;
    onContextMenu: (event: React.MouseEvent<Element, MouseEvent>) => void;
    onTouchStart: (event: React.TouchEvent<Element>) => void;
    onTouchEnd: () => void;
    onClick: (mouseEvent: React.MouseEvent<Element, MouseEvent>) => void;
    style: React.CSSProperties;
  };
} => {
  const { longTouchDurationMs } = props;
  const [contextEvent, setContextEvent] = useState<ContextEvent | null>(null);

  useEffect(() => {
    if (typeof window === "undefined") return;

    // Needed to make it go away when clicking outside of the popper, doesn't work for textarea though
    window.addEventListener("click", () => {
      setContextEvent(null);
    });
  }, []);

  const contextProps = useContext(
    (contextEvent) => {
      setContextEvent(contextEvent);
      console.log("callback called", props, contextEvent);
    },
    { longTouchDurationMs }
  );

  const virtualReference = useMemo(() => {
    if (!contextEvent) {
      //   console.log("NO context event,SHOULD NOT HAPPEN");
      return;
    }

    // console.log("YAY");

    // console.log({
    //   contextEvent,
    //   x: contextEvent?.clientX,
    //   y: contextEvent?.clientY,
    // });
    return {
      // This is going to create a virtual reference element

      getBoundingClientRect() {
        // console.log({ contextEvent });

        const clientRect: ClientRect = {
          top: contextEvent?.clientY || 10,
          left: contextEvent?.clientX || 10,
          bottom: 0,
          right: 0,
          width: 0,
          height: 0,
          x: 0,
          y: 0,
          toJSON() {
            return "";
          },
        };
        return clientRect;
      },
    };
  }, [contextEvent]);

  const popperRef = useRef<HTMLDivElement>(null);
  const { styles, attributes } = usePopper(
    virtualReference,
    popperRef.current,
    {
      strategy: "fixed",
      placement: "auto-start",
      modifiers: [{ name: "preventOverflow", enabled: false }],
    }
  );

  const renderContextPopper = () =>
    virtualReference && contextEvent ? (
      <div
        onClick={(e) => {
          // NB: needed to ensure it doesn't close due to the window.click
          e.stopPropagation();
        }}
        style={styles.popper}
        {...attributes.popper}
      >
        {props.renderPopper({ id: contextEvent.id })}
      </div>
    ) : null;

  const openContextPopperProps = {
    ...contextProps,
    ref: popperRef,
  };

  const onClose = () => setContextEvent(null);

  return {
    openContextPopperProps,
    renderContextPopper,
    onClose,
    isOpen: !!contextEvent,
  };
}

useContext.rawText:
 (
  /**
   * The callback that activates whenever you right-click or long-press
   */
  callback: (position: ContextEvent) => any,
  config?: {
    /**
     * Customise the time for a longpress to enable the callback. Defaults to browser default (onContextMenu default). If you set this, we detect the duration ourselves via the onTouchStart and onTouchEnd events.
     */
    longTouchDurationMs?: number;
  }
) => {
  const [timeoutRef, setTimeoutRef] = useState<NodeJS.Timeout | null>(null);

  const openTouchMenu = (event: React.TouchEvent) => {
    const touch = event.touches.item(event.touches.length - 1);
    const { clientX, clientY, screenX, screenY } = touch;
    const id = event.currentTarget?.id;
    callback({ clientX, clientY, screenX, screenY, id });
    setTimeoutRef(null);
  };

  const openContextMenu = (event: React.MouseEvent) => {
    const { clientX, clientY, screenX, screenY } = event;
    const id = event.currentTarget?.id;
    callback({ clientX, clientY, screenX, screenY, id });
  };

  const onTouchStart = (event: React.TouchEvent) => {
    // Only allow for a single timeout
    if (timeoutRef) return;
    if (!config?.longTouchDurationMs) return;
    const timeout = setTimeout(
      () => openTouchMenu(event),
      config.longTouchDurationMs
    );
    setTimeoutRef(timeout);
  };

  /**
   * Required for ensuring that you don't close  the menu immediately when opening it on mobile, because a click-event is also fired there
   */
  const onClick = (mouseEvent: React.MouseEvent) => {
    // this also makes it impossible to close big markdown files!
    // mouseEvent.stopPropagation();
  };

  const onTouchEnd = () => {
    if (timeoutRef) {
      clearTimeout(timeoutRef);
    }
  };

  const onContextMenu = (event: React.MouseEvent) => {
    event.preventDefault();
    // NB: needed to stop it from opening anywhere above (mobile)
    event.stopPropagation();
    openContextMenu(event);
  };
  //
  const contextProps = {
    onContextMenu,
    // onContextMenuCapture: (event: React.MouseEvent) => {
    //   event.preventDefault();
    //   event.stopPropagation();
    // },
    onTouchStart,
    onTouchEnd,
    onClick,
    style: {}, //userSelect: "none" as React.CSSProperties["userSelect"]
  };
  return contextProps;
}

convertToTargetCasing.rawText:
 (word: string, index: number, target: Casing) => {
  if (target === "capital") return word.toUpperCase();
  if (target === "kebab" || target === "snake") return word.toLowerCase();
  if (target === "pascal") return capitaliseFirstLetter(word);
  if (target === "camel")
    return index === 0 ? word.toLowerCase() : capitaliseFirstLetter(word);

  //human case
  return index === 0 ? capitaliseFirstLetter(word) : word.toLowerCase();
}

getDelimiter.rawText:
 (target: Casing) => {
  if (target === "capital") return "_";
  if (target === "human") return " ";
  if (target === "kebab") return "-";
  if (target === "snake") return "_";
  return "";
}

splitCasingDelimiters.rawText:
 (word: string): string[] => {
  const letters = word.split("");
  const allWords = letters.reduce(
    (words, letter) => {
      //get the last word, we know it's always defined because of the initial value of the reduce
      const lastWord: string = words.pop()!;
      //let's also get the last letter
      const lastLetter = lastWord.substring(-1);
      const lastLetterIsLowercase = lastLetter.toUpperCase() !== lastLetter;
      const letterIsUppercase = letter.toUpperCase() === letter;
      const createNewWord = lastLetterIsLowercase && letterIsUppercase;
      const newSequence = createNewWord
        ? [lastWord, letter]
        : [`${lastWord}${letter}`];
      const newWords = words.concat(newSequence);
      return newWords;
    },
    [""]
  );

  return allWords;
  // if it was lowercase but it became upper, it's a new word
}

