showStandardResponse.rawText:
 (apiResult: StandardizedApiReturnType) => {
  if (apiResult.errors) {
    console.log({ errors: apiResult.errors });
  }

  if (apiResult.isUnauthorized) {
    errorToast(apiResult.message);
    return;
  }

  if (apiResult.isNotConnected) {
    warningToast(apiResult.message || "Not connected");
    return;
  }

  if (!apiResult.isSuccessful) {
    errorToast(apiResult.message || "Something went wrong");
    return;
  }

  if (!apiResult.result) {
    successToast("Done");
    return;
  }

  if (!apiResult.result.isSuccessful) {
    errorToast(apiResult.result.message || "Something went wrong");
    return;
  }

  successToast(apiResult.result.message || "Done");
}

