CrudTable.rawText:
 (props: CrudViewProps) => {
  const { tsInterface, onEndReached, data, highlight, actions } = props;

  const alert = useAlert();
  const properties = getProperties(tsInterface?.type?.typeDefinition);
  const dataParameterNames = getPropertiesDataParameterNames(properties);

  const columns: ColumnType<any>[] = properties
    // Omit referenced data
    .filter((property) => !dataParameterNames.includes(property.name))
    .map((property) => {
      const referenceParameterInfo = getReferenceParameterInfo(property.name);
      const {
        descriptor,
        interfaceName,
        isReferenceMultipleParameter,
        isReferenceParameter,
        isReferenceSingleParameter,
      } = referenceParameterInfo;

      const isBackendAssetParameter = getAssetInputType(property.name);
      const presentationType = isReferenceSingleParameter
        ? "referenceSingle"
        : isReferenceMultipleParameter
        ? "referenceMultiple"
        : isBackendAssetParameter
        ? "backendAsset"
        : "text";

      const name =
        isReferenceParameter && interfaceName
          ? descriptor
            ? humanCase(`${descriptor}-${interfaceName}`)
            : humanCase(interfaceName)
          : humanCase(property.name);

      const column: ColumnType<any> = {
        name,
        objectParameterKey: property.name,
        presentationType,
      };
      return column;
    })
    .filter(notEmpty);

  return (
    <Table
      data={data}
      onEndReached={onEndReached}
      shouldHighlightItem={(item: any) =>
        !!(
          (highlight.id && item.id === highlight.id) ||
          (highlight.slug && item.slug === highlight.slug)
        )
      }
      columns={columns}
      renderExtraColumns={(item) => {
        return (
          <Div className="flex flex-row">
            {item
              ? actions.map((action) => {
                  return (
                    <LabeledButton
                      onClick={() => action.action(item)}
                      title={action.name}
                      emoji={action.emoji}
                      size="small"
                    />
                  );
                })
              : null}
          </Div>
        );
      }}
      extraColumnsAtStart
    />
  );
}

DatasetForm.rawText:
 (props: { modelName: string }) => {
  const { modelName } = props;

  const [datasetConfig, setDatasetConfig] = useStore("db-crud.datasetConfig");

  const [form, datasetConfigForm] = useTsInterfaceForm(
    DatasetConfigIndex as Storing<TsInterface>,
    datasetConfig?.id || "noid",
    datasetConfig
  );

  const addDatasetFromDatasetConfig = async () => {
    if (!modelName) return;

    let name = prompt("What should be the name?", "untitled");
    const dataset: Creation<Dataset> = {
      ...datasetConfigForm,
      modelName,
      name: name || "untitled",
    };

    const { isSuccessful, result, message } = await api.upsertDbModel(
      "Dataset",
      //@ts-ignore
      dataset,
      true
    );

    metadataQuery.refetch();

    successToast(
      isSuccessful && result?.isSuccesful
        ? "Succesfully added"
        : result?.message || message || "Error"
    );
  };

  const metadataQuery = queries.useGetDbModelMetadata(modelName as DbModelEnum);
  const { datasets, tsInterface } = destructureOptionalObject(
    metadataQuery.data?.result
  );

  const removeDataset = async () => {
    if (!datasetConfig?.id) return;

    const { isSuccessful, result, message } = await api.deleteDbModel(
      "Dataset",
      datasetConfig.id
    );

    // refetch datasets
    metadataQuery.refetch();
    setDatasetConfig(null);

    successToast(
      isSuccessful && result?.isSuccesful
        ? "Succesfully removed"
        : result?.message || message || "Error"
    );
  };

  const properties = getProperties(tsInterface?.type?.typeDefinition);

  return (
    <Div className="border rounded-md border-gray-700">
      <P className="text-3xl">Dataset Configuration</P>

      {form}

      <ClickableIcon
        emoji="‚ûï New dataset"
        onClick={addDatasetFromDatasetConfig}
      />

      <ClickableIcon
        emoji="üß™ Apply"
        onClick={() =>
          datasetConfigForm !== undefined
            ? setDatasetConfig(datasetConfigForm)
            : null
        }
      />

      <ClickableIcon
        emoji="üßπ Clear dataset"
        onClick={() => setDatasetConfig(null)}
      />
      {datasetConfig?.id ? (
        <ClickableIcon emoji="üóë Remove dataset" onClick={removeDataset} />
      ) : null}
    </Div>
  );
}

