ModelComponent.rawText:
 (props: {
  modelName?: string;
  highlight: Highlight;
}) => {
  const { modelName, highlight } = props;
  const alert = useAlert();
  const router = useRouter();

  const views = modelViews.map((modelView) => ({
    value: modelView.view,
    label: `${modelView.emoji} ${modelView.view}`,
  }));

  const [SelectView, viewItem] = useSelect(views, views[0]);
  const view = viewItem!.value;

  const metadataQuery = queries.useGetDbModelMetadata(modelName as DbModelEnum);
  const { datasets, tsInterface } = destructureOptionalObject(
    metadataQuery.data?.result
  );

  const datasetItems = datasets?.map((dataset) => ({
    label: dataset.name,
    value: dataset.id,
    data: dataset,
  }));

  const datasetSelectItems: Item<Dataset>[] = [
    { value: "", label: "Select a dataset" },
    ...(datasetItems || []),
    { value: "new", label: "(+) New dataset" },
  ];

  const [SelectDataset] = useSelect(
    datasetSelectItems,
    undefined,
    (newValue) => {
      if (newValue?.value === "new") {
        // show a blank screen
        setDatasetConfig({ key: `config${Math.random()}` });
        return;
      }

      if (newValue?.value === "") {
        setDatasetConfig(null);
        return;
      }

      if (newValue?.data) {
        setDatasetConfig({ ...newValue.data, key: `config${Math.random()}` });
        return;
      }
    }
  );

  const [datasetConfig, setDatasetConfig] = useStore("db-crud.datasetConfig");

  const model = useInfiniteGetDbModel();
  const modelReferences = queries.useGetReferencableModelData(
    modelName as DbModelEnum
  );

  const isLoading = model.isLoading || model.isRefetching || model.isFetching;

  const allData = model?.data?.pages
    .map((x) => x.result?.data)
    .flat()
    .filter(notEmpty);

  // const  count = sum(model.data?.pages.map((x) => x.result?.data.length || 0) || []);

  const indexDescription = tsInterface ? (
    <Div>
      <P className="font-bold">{tsInterface.name}</P>

      <MarkdownContent
        content={tsInterface.description || "no description"}
        config={{
          projectRelativeBaseFolderPath: getFolderJs(
            tsInterface.projectRelativePath
          ),
          projectRelativeMarkdownFilePath: tsInterface.projectRelativePath,
        }}
      />
    </Div>
  ) : isLoading ? (
    <Div />
  ) : (
    "No index found"
  );

  const headerButtons = (
    <Div className="flex flex-row items-center">
      <LabeledButton
        onClick={() => router.push(`/upsert/${modelName}`)}
        title="New"
        emoji="‚ûï"
      />

      <LabeledButton
        {...{
          onClick: () => model.refetch(),
          title: "Reload",
          emoji: isLoading ? undefined : "üîÑ",
          component: isLoading ? () => <FancyLoader medium /> : undefined,
        }}
      />

      {isBetaEnabled ? <SelectView /> : null}

      {isBetaEnabled ? <SelectDataset /> : null}

      <SearchBar />
    </Div>
  );

  const onEndReached = () => {
    const pages = model.data?.pages;

    const lastPage = pages ? pages[pages.length - 1] : undefined;

    const hasMore = lastPage?.result?.hasMore;

    if (hasMore && !model.isFetchingNextPage) {
      model.fetchNextPage();
    }
  };

  const deleteItem = (item: AugmentedAnyModelType) => {
    alert?.("Are you sure?", "Do you want to delete this one?", [
      {
        text: "Yes",
        style: "destructive",
        onPress: () => {
          if (item?.id) {
            // console.log({ id: item.id });
            deleteDbModel(modelName as any, item.id).then((result) => {
              model.refetch();
              modelReferences.refetch();
            });
          }
        },
      },
      { text: "Cancel", style: "cancel" },
    ]);
  };

  const deleteAction: ItemAction = {
    action: deleteItem,
    emoji: "‚ùå",
    name: "Delete",
  };

  const updateItem = (item: AugmentedAnyModelType) =>
    router.push(`/upsert/${modelName}?id=${item?.id}`);

  const updateAction: ItemAction = {
    name: "Update",
    emoji: "‚úèÔ∏è",
    action: updateItem,
  };

  const actions: ItemAction[] = [deleteAction, updateAction];

  const CrudView = {
    table: CrudTable,
    grid: CrudGrid,
    timeline: CrudTimeline,
    tree: CrudTree,
  }[view];

  const crudViewProps: CrudViewProps = {
    actions,

    //@ts-ignore
    data: allData,
    highlight,
    tsInterface,
    onEndReached,
  };

  return (
    <Div>
      <Div className="px-8 lg:px-20 py-4">
        {headerButtons}
        {indexDescription}

        {datasetConfig && modelName && isBetaEnabled ? (
          <DatasetForm key={datasetConfig.key} modelName={modelName} />
        ) : null}
      </Div>

      {/* NB: here a table view should be rendered */}
      {Array.isArray(allData) && allData.length > 0 && CrudView ? (
        <CrudView {...crudViewProps} />
      ) : null}
    </Div>
  );
}

