getDbModelMetadata.rawText:
 async (
  modelName: string | undefined
): Promise<{
  tsInterface?: TsInterface;
  datasets?: Dataset[];
  projectRelativeStorageFilePath?: string;
}> => {
  if (!modelName) return {};
  if (!([...dbModelKeys] as string[]).includes(modelName)) {
    return {};
  }

  const realModelName = modelName as DbModelEnum;

  const mergedConfig: MergedQueryConfig = getMergedQueryConfig(realModelName);
  const projectRelativeStorageFilePath = (
    await getDatabaseFiles(realModelName, mergedConfig)
  ).map((x) => x.projectRelativePath)[0];

