  if (bundleId) {
    const bundleConfig = (await db.get("BundleConfig")).find(
      (x) => x.id === bundleId
    );

    if (bundleConfig) {
      dbModels = await getDbModelsForBundle(bundleConfig);
    }
  }
  const projectRoot = getProjectRoot();
  if (isBundle(projectRoot) && projectRoot) {
    const operationNames: string[] = (
      await exploreOperationFolders({
        basePath: path.join(projectRoot, "packages"),
      })
    ).map(getLastFolder);

    dbModels = await getDbModelsFromOperations(operationNames);
  }

  const dbModelNames = dbModels
    ? (dbModels?.map((x) => x.name) as (keyof DbModels)[])
    : [...dbModelKeys];

  return dbModelNames;
}

getDbModel.rawText:
 async <
  KInterface extends Extract<keyof DbModels, string>,
  TDatasetConfig extends DatasetConfig
>(
  /**
   * the interfaceName you want to get
   */
  interfaceName: KInterface | null,
  /**
   * optionally, provide a configuration
   */
  datasetConfig?: TDatasetConfig,

  /**
   * This search should be done on the deepest JSON value's of the whole thing. The purpose is not limiting the content to user, but rather just a nice user experience where one can quickly search
   */
  search?: string
): Promise<GetDbModelResult<KInterface>> => {
  if (!interfaceName) {
    return { data: [], hasMore: false, message: "No interfaceName posted" };
  }

  const data = await db.get(interfaceName);

  // NB: slice the data, if needed
  const slicedStartData = data.slice(datasetConfig?.startFromIndex);

  const slicedLimitData = datasetConfig?.maxRows
    ? slicedStartData.slice(0, datasetConfig.maxRows)
    : slicedStartData;

  const hasMore = slicedLimitData.length < slicedStartData.length;

  // NB: filter the sliced data, if needed
  const filteredData = datasetConfig?.filter?.length
    ? datasetConfig?.filter.reduce((filteredData, datasetFilter) => {
        const newFilteredData: DbModels[KInterface][] = filteredData.filter(
          (item) => {
            const key = datasetFilter.objectParameterKey as keyof typeof item;

            const value = item[key];

            if (datasetFilter.operator === "equal") {
              return String(value) === datasetFilter.value;
            }

            if (datasetFilter.operator === "notEqual") {
              return String(value) === datasetFilter.value;
            }

            const lowercaseValue = String(value).toLowerCase();
            const lowercaseDatasetValue = String(
              datasetFilter.value
            ).toLowerCase();

            if (datasetFilter.operator === "endsWith") {
              return lowercaseValue.endsWith(lowercaseDatasetValue);
            }
            if (datasetFilter.operator === "startsWith") {
              return lowercaseValue.startsWith(lowercaseDatasetValue);
            }

            if (datasetFilter.operator === "includes") {
              return lowercaseValue.includes(lowercaseDatasetValue);
            }

            if (datasetFilter.operator === "includesLetters") {
              return hasAllLetters(lowercaseValue, lowercaseDatasetValue);
            }

            if (
              datasetFilter.operator === "greaterThan" &&
              datasetFilter.value !== null &&
              datasetFilter.value !== undefined
            ) {
              return Number(value) > Number(datasetFilter.value);
            }

            if (
              datasetFilter.operator === "lessThan" &&
              datasetFilter.value !== null &&
              datasetFilter.value !== undefined
            ) {
              return Number(value) < Number(datasetFilter.value);
            }

            if (
              datasetFilter.operator === "greaterThanOrEqual" &&
              datasetFilter.value !== null &&
              datasetFilter.value !== undefined
            ) {
              return Number(value) >= Number(datasetFilter.value);
            }

            if (
              datasetFilter.operator === "lessThanOrEqual" &&
              datasetFilter.value !== null &&
              datasetFilter.value !== undefined
            ) {
              return Number(value) <= Number(datasetFilter.value);
            }

            return false;
          }
        );

        return newFilteredData;
      }, slicedLimitData)
    : slicedLimitData;

  // NB: sort the filtered data, if needed

  const sortedData = datasetConfig?.sort
    ? datasetConfig.sort.reduce((sortedData, datasetSort) => {
        const newSortedData: DbModels[KInterface][] = sortedData.sort(
          (a, b) => {
            // @ts-ignore
            const valueA = a[datasetSort.objectParameterKey];
            // @ts-ignore
            const valueB = b[datasetSort.objectParameterKey];

            const directionMultiplier =
              datasetSort.sortDirection === "ascending" ? 1 : -1;

            return Number(valueA) < Number(valueB)
              ? directionMultiplier
              : directionMultiplier * -1;
          }
        );

        return newSortedData;
      }, filteredData)
    : filteredData;

  const searchedData =
    search && search.length > 0
      ? sortedData.filter((item) => {
          const searchable = Object.values(item)
            .map((value) => JSON.stringify(value))
            .join(",")
            .toLowerCase();

          return searchable.includes(search.toLowerCase());
        })
      : sortedData;

  const subsetData = datasetConfig?.objectParameterKeys?.length
    ? searchedData.map(
        (item) =>
          getSubsetFromObject(
            item,
            datasetConfig.objectParameterKeys! as readonly (keyof DbModels[KInterface])[]
          ) as DatasetItem<DbModels[KInterface], TDatasetConfig>
      )
    : searchedData;

  const ignoredData = datasetConfig?.ignoreObjectParameterKeys?.length
    ? subsetData.map((item) => {
        return removeOptionalKeysFromObjectStrings(
          item as { [key: string]: any },
          datasetConfig.ignoreObjectParameterKeys!
        );
      })
    : subsetData;

  const finalData = ignoredData as DbModels[KInterface][];

  return {
    datasetConfig,
    data: finalData,
    hasMore,
  };
}

getFunctionIndex.rawText:
 async ({
  functionName,
}: {
  functionName: string;
}): Promise<{ success: boolean; response: string; function?: TsFunction }> => {
