upsertDbModel.rawText:
 async <
  KInterfaceName extends keyof DbModels,
  KItem extends DbModels[KInterfaceName]
>(
  interfaceName: KInterfaceName,
  data: KItem | KItem[],
  /**
   * NOT USED
   */
  isNew?: boolean
): Promise<DbQueryResult> => {
  if (!data || (Array.isArray(data) && data.length === 0)) {
    return { isSuccesful: false, message: "Please provide some data" };
  }

  // use the first item to get the operationName, sometimes needed for storing in the right location
  const item = Array.isArray(data) ? data[0] : data;
  const { operationName } = item;

  /**

  Assets need to be placed: 

  1) find all assets in data

  2) for every parameter that adheres to asset naming convention, call `processAsset`

  */

  const config: CustomQueryConfig = { operationName };

  const realData = (await Promise.all(
    makeArray(data).map((item) => {
      return processItemAssets(item, interfaceName, operationName, config);
    })
  )) as KItem[];

  // console.dir({ realData }, { depth: 999 });
  // return { message: "test" };
  //@ts-ignore
  const upsertResult = await db.upsert(interfaceName, realData, config);

  return upsertResult;
}

