docsGetPages.rawText:
 async (
  basePaths: { projectRelativeBasePath: string; queryPath: string }[]
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return [];
  }

  const realProjectRelativeBasePaths = isBundle()
    ? ["docs"]
    : basePaths.map((x) => x.projectRelativeBasePath);

  const absoluteBasePaths = realProjectRelativeBasePaths.map((x) =>
    path.join(projectRoot, x)
  );

  const flat =
    (await getMarkdownReaderPages({
      projectRoot,
      basePaths: absoluteBasePaths,
      mapQueryPath: (queryPath) => {
        const found = realProjectRelativeBasePaths.find((x) =>
          queryPath.startsWith(x)
        );
        if (!found) {
          return queryPath;
        }
        const prefix = basePaths.find(
          (x) => x.projectRelativeBasePath === found
        );

        if (!prefix) {
          return queryPath;
        }

        const newQueryPath = queryPath.replace(
          prefix.projectRelativeBasePath,
          prefix.queryPath
        );

        return newQueryPath;
      },
    })) || [];

  return flat;
}

getMarkdownReaderPages.rawText:
 async (config: {
  projectRoot: string;
  /**
   * basePaths to search for public markdown for
   */
  basePaths: string[];

  /**
   * If given, uses a basePath relative queryPath and prefixes this before it.
   *
   * if there's a prefix, don't use the basePath as prefix, but use that prefix instead.
   *
   * Should end with slash (/) in order for it to be folder
   */
  queryPathCustomPrefix?: (basePath?: string) => string | undefined;
  /**
   * if set, maps query path further
   */
  mapQueryPath?: (
    /**
     * already mapped the relative path to remove extension and numbers from folders
     */
    queryPath: string
  ) => string;
}): Promise<ReaderWebPage[]> => {
  const { basePaths, projectRoot, mapQueryPath } = config;
  const markdownReaderPages: ReaderWebPage[] = (
    await Promise.all(
      basePaths.map(async (basePath) => {
        const publicMarkdownFileAbsolutePaths =
          await getPublicMarkdownFilePaths(basePath, false);

        const markdownReaderPages = publicMarkdownFileAbsolutePaths.map((x) => {
          const filePath = makeRelative(x.path, projectRoot);
          const pathWithoutReadme = stripReadmeFromFolder(filePath);
          const pathWithoutExtensions =
            removeExtensionsFromPath(pathWithoutReadme);

          const mappedQueryPath = mapQueryPath
            ? mapQueryPath(pathWithoutExtensions)
            : pathWithoutExtensions;

          const customQueryPrefix = config.queryPathCustomPrefix
            ? config.queryPathCustomPrefix(basePath)
            : undefined;

          const relativeBasePath = makeRelative(basePath, projectRoot);

          // console.log({ mappedQueryPath, relativeBasePath });
          const queryPath = customQueryPrefix
            ? customQueryPrefix + mappedQueryPath.replace(relativeBasePath, "")
            : mappedQueryPath;

          // NB: folders are no menu items because menu is built from queryPaths recursively
          const readerWebPage: ReaderWebPage = {
            queryPath,
            pageData: { projectRelativeFilePath: filePath },
            isMenuHidden: x.isFolder,
          };

          return readerWebPage;
        });

        return markdownReaderPages;
      })
    )
  ).flat();

  return markdownReaderPages;
}

getPublicMarkdownFilePaths.rawText:
 async (
  baseFolderPath: string,
  includeFoldersWithResults?: boolean
) => {
  const publicMarkdownFilePaths: { path: string; isFolder: boolean }[] = (
    await explore({
      basePath: baseFolderPath,
      extension: "md",
      readmeOnTop: true,
      includeFoldersWithResults,
    })
  )
    .filter((x) => {
      if (x.isFolder) return true;

      // NB: we have a file
      const markdownString = fs.readFileSync(x.path, "utf8");
      const { parameters } = parseFrontmatterMarkdownString(markdownString);
      if (!shouldExposeMarkdownFile(parameters)) {
        return false;
      }
      return true;
    })
    .map(({ path, isFolder }) => ({ path, isFolder }));

  return publicMarkdownFilePaths;
}

removeExtensionsFromPath.rawText:
 (
  /**
   * should also work for filenames
   */
  relativePath: string
): string => {
  const chunks = trimSlashes(relativePath).split("/");

  const chunksWithoutNumbers = chunks.map(removeNumberPrefix);

  // could be file
  const lastChunkIndex = chunksWithoutNumbers.length - 1;

  const lastChunk = chunksWithoutNumbers[lastChunkIndex];
  const parts = lastChunk.split(".");
  const lastPart = parts[parts.length - 1];

  if (availableExtensions.includes(lastPart)) {
    // remove extension
    parts.pop();
    chunksWithoutNumbers[lastChunkIndex] = parts.join(".");
  }

  const simplifiedPath = chunksWithoutNumbers.join("/");

  return simplifiedPath;
}

removeNumberPrefix.rawText:
 (fileOrFolderName: string): string => {
  const parts = fileOrFolderName.split(".");

  const newName = parts.reduce((restName, part, currentIndex, array) => {
    if (restName) return restName;
    const isTextPart = isNaN(Number(part));

    if (isTextPart) {
      // NB: everything after here is supposed to be part of the text
      const restName = array.slice(currentIndex).join(".");
      return restName;
    }
  }, undefined as string | undefined);

  return newName || "untitled";
}

stripReadmeFromFolder.rawText:
 (filePath: string): string => {
  const suffix = "/readme.md";

  if (filePath.toLowerCase().endsWith(suffix)) {
    const strippedPath = filePath.slice(0, filePath.length - suffix.length);

    return strippedPath;
  }

  return filePath;
}

