exploreOperation.rawText:
 async (operationBasePath: string) => {
  const srcPath = path.join(operationBasePath, "src");
  const docsPath = path.join(operationBasePath, "docs");

  const rootExploreResults = await explore({
    basePath: operationBasePath,
    ignore: ".DS_Store",
    doNotExploreChildFolders: true,
    extension: ["md", "mdx"],
  });

  const docsExploreResults = await explore({
    basePath: docsPath,
    ignore: ".DS_Store",
    extension: ["md", "mdx"],
    includeFoldersWithResults: true,
  });

  const docsExploreResultsWithBasePath =
    docsExploreResults.length > 0
      ? docsExploreResults.concat([{ path: docsPath, isFolder: true }])
      : [];

  const srcExploreResults = (
    await explore({
      basePath: srcPath,
      ignore: ".DS_Store",
      extension: ["ts", "tsx"],
      includeFoldersWithResults: true,
    })
  )
    // NB: filter out index files, they're not interesting for this usecase
    .filter((x) => !x.path.includes("/src/index.ts"));

  const srcExploreResultsWithBasePath =
    srcExploreResults.length > 0
      ? srcExploreResults.concat([{ path: srcPath, isFolder: true }])
      : [];

  return rootExploreResults
    .concat(docsExploreResultsWithBasePath)
    .concat(srcExploreResultsWithBasePath);
}

exploreProject.rawText:
 async (config?: {
  bundleId?: string;
}): Promise<FolderExploration[] | undefined> => {
  const bundleId = config?.bundleId;
  let bundleSummary: BundleSummary | undefined = undefined;

  if (bundleId) {
    const bundle = (await db.get("BundleConfig")).find(
      (x) => x.id === bundleId
    );

    if (bundle) {
      bundleSummary = getBundleSummary(bundle);
    }
  }

  const operationPaths = getPathsWithOperations();
  const textPath = getRootPath("text");
  const projectRoot = getProjectRoot();
  if (!operationPaths || !textPath || !projectRoot) return;
  /**
   * STEP 1: finds all folders and files unto (and including) the operation level
   */
  const foldersWithMarkdownAndOperationsExplore = await explore({
    basePath: operationPaths.concat(textPath),
    includeFoldersWithResults: true,
    ignore: [
      "node_modules",
      ".git",
      databaseFolderName,
      ".gitignore",
      ".DS_Store",
    ],
    // extension: ["md","mdx"]
    cancelRecursionOn: pathArrayIsOperation,
  });

  /**
   * STEP 2: explore all paths to include the files of operations
   */
  const exploreArrayPromises = foldersWithMarkdownAndOperationsExplore.map(
    async (exploreResult) => {
      // NB: Here I am looking if the operationName is in the bundle summary. If there's a bundle summary and this is not the case, the operation should not be explored
      const operationName = getLastFolder(exploreResult.path);
      const shouldIncludeOperation = !bundleSummary
        ? true
        : bundleSummary.packageNames.includes(operationName);

      const exploreResultArray = exploreResult.isCancelRecursionResult
        ? shouldIncludeOperation
          ? await exploreOperation(exploreResult.path)
          : []
        : [exploreResult];

      return exploreResultArray;
    }
  );
  const resultPaths = (await Promise.all(exploreArrayPromises))
    .flat()
    .map((x) => x.path);

  /**
   * STEP 3: get all data of all files
   */
  const indexData = {
    functions: await db.get("TsFunction"),
    variables: await db.get("TsVariable"),
    interfaces: await db.get("TsInterface"),
    // comments: await db.get("TsComment"),
    // "build-errors": await db.get("TsBuildError"),
  };

  const operationFolders: string[] = await exploreOperationFolders({
    basePath: getPathsWithOperations(),
  });

  const fullIndexation = resultPaths
    .map((absolutePath): FolderExploration | null => {
      const relativeProjectPath = absolutePath.substring(projectRoot.length);
      // extension like .ts or empty string if there's no extensnion (also for folders)
      const name = path.parse(absolutePath).name;
      const type = getExplorationType(absolutePath, operationFolders);

      // Not typescript
      if (type !== "typescript") {
        // name of folder, operation, operationFolder, or md file
        // NB: shouldn't happen
        if (!type) return null;
        // NB: we are filling in the children in a later step
        return { name, relativeProjectPath, type, children: undefined };
      }

      // Typescript

      const typescriptFileChildren = (
        Object.keys(indexData) as (keyof typeof indexData)[]
      )
        .map((indexInstanceName): FolderExploration[] => {
          const all = indexData[indexInstanceName];
          const indexNames = getInstanceNames(all, relativeProjectPath);
          const type =
            indexInstanceName === "functions"
              ? "function"
              : indexInstanceName === "interfaces"
              ? "interface"
              : "variable";

          const instanceChildren: FolderExploration[] = indexNames.map(
            (name) => ({ name, relativeProjectPath, type })
          );

          return instanceChildren;
        })
        .flat();

      return {
        name,
        relativeProjectPath,
        type,
        children: typescriptFileChildren,
      };
    })
    .flat()
    .filter(notEmpty);

  //
  const oppositeSortedFullIndexation = fullIndexation
    // sort alphabetically on relativeProjectPath
    .sort((a, b) => {
      if (a.relativeProjectPath < b.relativeProjectPath) return -1;
      if (a.relativeProjectPath > b.relativeProjectPath) return 1;
      return 0;
    })
    // reverse the result so it's in opposite alphabetical order
    .reverse();

  // NB: copy because we want to test stuff first
  const copyForNested = [...oppositeSortedFullIndexation];
  const nestedExploration = copyForNested
    .reduce((all, folderExploration, currentIndex) => {
      const parsedPath = path.parse(
        path.join(projectRoot, folderExploration.relativeProjectPath)
      );

      /** the parent folder of a folder, or the folder of a path */
      const baseFolder =
        parsedPath.base.length > 0
          ? parsedPath.dir
          : path.join(parsedPath.dir, "..");
      const relativeBaseFolder = baseFolder.substring(projectRoot.length);

      // console.log({
      //   relativeBaseFolder,
      //   relativeProjectPath: folderExploration.relativeProjectPath,
      // });
      const baseIndex = all.findIndex(
        (x) => x?.relativeProjectPath === relativeBaseFolder
      );

      // there is no parent found for this one
      if (baseIndex === -1) {
        return all;
      }

      // we found a parent on all[baseIndex]. Lets put folderExploration in the children of that one, and remove it from the full indexation

      all[baseIndex] = {
        ...all[baseIndex],
        children: all[baseIndex].children
          ? all[baseIndex].children?.concat(folderExploration)
          : [folderExploration],
      };

      delete all[currentIndex];

      return all;
    }, copyForNested as FolderExploration[])
    // the deleted keys can now be removed
    .filter(notEmpty);

  // console.dir(
  //   oppositeSortedFullIndexation.map((x) => x.relativeProjectPath),
  //   { depth: 999, maxArrayLength: 9999 }
  // );

  return nestedExploration;
}

getExplorationType.rawText:
 (
  absolutePath: string,
  operationFolders: string[]
): FolderExploration["type"] | undefined => {
  const parsedPath = path.parse(absolutePath);
  if ([".ts", ".tsx"].includes(parsedPath.ext)) {
    return "typescript";
  }
  if ([".md", ".mdx"].includes(parsedPath.ext)) {
    return "markdown";
  }

  if (parsedPath.ext.length > 0) {
    // NB: based on where this function is used, this should never happen
    return undefined;
  }

  if (operationFolders.includes(absolutePath)) {
    return "operation";
  }
  if (operationFolders.find((x) => absolutePath.startsWith(x))) {
    return "operationFolder";
  }
  return "folder";
}

getFolderExplorationDetails.rawText:
 async (
  config: {
    /**relative project path*/
    path: string;
    type: FolderExploration["type"];
    name: string;
    sort?: string;
    /**
     * null gives all of them, undefined gives none
     */
    typeIndexType?: keyof IndexModels | null;
  } & IndexFilter
): Promise<ExplorationDetails> => {
  const {
    name,
    path,
    type,
    hasCommentTypes,
    interfaceIsDbModel,
    sort,
    typeIndexType,
  } = config;
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    console.log("getFolderExplorationDetails: Projectroot not found");
    process.exit(1);
  }
  const absolutePath = nodePath.join(projectRoot, path);
  const operationBasePath = findOperationBasePath(absolutePath);

  const operationFolderName = operationBasePath
    ? getLastFolder(operationBasePath)
    : undefined;

  const folderExplorationInterface = (
    await db.get("TsInterface", {
      operationName: "code-types",
    })
  ).filter(
    (x) => x.operationRelativeTypescriptFilePath === "src/FolderExploration.ts"
  );

  const isFileOrFolderType = [
    "folder",
    "operation",
    "operationFolder",
    "typescript",
  ].includes(type);

  const pathMetaData = isFileOrFolderType
    ? await calculatePathMetaData(absolutePath)
    : undefined;

  const needsFilePath =
    !!operationFolderName && absolutePath !== operationBasePath;

  // console.log({
  //   needsFilePath,
  //   operationFolderName,
  //   absolutePath,
  //   operationBasePath,
  //   typeIndexType,
  //   isFileOrFolderType,
  // });
  const typescriptIndex =
    isFileOrFolderType && typeIndexType !== undefined
      ? await getFolderTypescriptIndex({
          filePath: needsFilePath ? absolutePath : undefined,
          basePath: !needsFilePath ? absolutePath : undefined,
          filter: { hasCommentTypes, interfaceIsDbModel },
          sort,
          type: typeIndexType,
        })
      : null;

  //   const srcPath = nodePath.join(operationBasePath, "src");
  //   const operationRelativeTypescriptFilePath = absolutePath.substring(srcPath.length + 1);

  if (type === "folder") {
    return {
      index: folderExplorationInterface,
      ...typescriptIndex,
      pathMetaData,
    };
  } else if (type === "operationFolder") {
    return {
      index: folderExplorationInterface,
      ...typescriptIndex,
      pathMetaData,
    };
  } else if (type === "operation" && operationBasePath) {
    return {
      index: folderExplorationInterface,
      ...typescriptIndex,
      pathMetaData,
    };
  } else if (type === "markdown") {
    // markdown parse
    const markdownPath = nodePath.join(projectRoot, path);
    const markdown = await readMarkdownFile(markdownPath);
    return {
      index: folderExplorationInterface,
      markdown: markdown || undefined,
      success: !!markdown,
      response: markdown ? undefined : "reading markdown file failed",
    };
  } else if (type === "typescript") {
    const details = typescriptIndex || {
      success: false,
      response: "reading typescript file failed",
    };

    return { ...details, index: folderExplorationInterface, pathMetaData };
    // all functions, interfaces, variables, lint-errors, build-errors, comments, file meta data (should become PathMetaData, better refactor immediately)
  } else if (type === "function") {
    // one function with name [name]
    // TODO: figure out how to do with specific file
    const tsFunction = (
      await db.get("TsFunction", { operationName: operationFolderName })
    ).find((x) => x.name === name);
    return {
      tsFunctions: tsFunction ? [tsFunction] : undefined,
      index: folderExplorationInterface,
    };
  } else if (type === "interface") {
    // one interface with name [name]
    const tsInterface = (
      await db.get("TsInterface", { operationName: operationFolderName })
    ).find((x) => x.name === name);
    return {
      tsInterfaces: tsInterface ? [tsInterface] : undefined,
      index: folderExplorationInterface,
    };
    // if it's a db-model, also the data that is found...? or maybe this should just be a separate endpoint and page where it's linke to
  } else if (type === "variable") {
    // one variable with name [name]
    const tsVariable = (
      await db.get("TsVariable", { operationName: operationFolderName })
    ).find((x) => x.name === name);
    return {
      tsVariables: tsVariable ? [tsVariable] : undefined,
      index: folderExplorationInterface,
    };
  }

  // Should never happen if all ifs  return....
  return {
    index: folderExplorationInterface,
    success: false,
    response: `getFolderExplorationDetails: Couldn't understand your input: ${JSON.stringify(
      {
        path,
        type,
        name,
      }
    )}`,
  };
}

getFrontmattersMappedObject.rawText:
 async (
