) => {
  const frontmatterPromises = markdownPaths?.map(async (markdownPath) => {
    const absoluteFilePath = path.join(projectRoot, markdownPath);
    const frontmatter = (await readMarkdownFile(absoluteFilePath))?.parameters;
    return { [markdownPath]: frontmatter };
  });
  const frontmattersArray = frontmatterPromises
    ? await Promise.all(frontmatterPromises)
    : [];
  const frontmattersMappedObject = mergeObjectsArray(frontmattersArray);

  return frontmattersMappedObject;
}

