getProjectRelativePaths.rawText:
 async (
  // functionContext: FunctionContext,
  config?: {
    /**
     * Optionally filter the results to only:
     *
     * - todo: all todos in your project operations
     * - postable: all your postables in your database
     *
     * by default, scans all relevant files...
     */
    type?: RelativePathType;
    earliestUpdatedAt?: number;
    /**
     * Not implemented
     */
    filterDraft?: boolean;
    /**
     * Not implemented
     */
    filterPrivate?: boolean;
    filterGenerated?: boolean;
    sort?: "recent";
  }
): Promise<string[] | undefined> => {
  const {
    earliestUpdatedAt,
    filterDraft,
    filterGenerated,
    filterPrivate,
    type,
    sort,
  } = destructureOptionalObject(config);

  const projectRoot = getProjectRoot();
  if (!projectRoot) return;
  const operationsPath = getRootPath("operations");
  if (!operationsPath) return;
  const databasePath = getRootPath(databaseFolderName);
  if (!databasePath) return;
  const textPath = getRootPath("text");
  if (!textPath) return;

  const basePath =
    type === "todo"
      ? await findAllTodoFolderPaths(operationsPath)
      : [operationsPath, textPath];

  const shouldFilterRecency = earliestUpdatedAt !== undefined;
  /*
  everything from projectRoot, except for: `db`, `node_modules`, `build`
  */

  const results = await explore({
    basePath,
    includeStats: shouldFilterRecency || sort === "recent",
    extension: ["md", "ts", "tsx"],
    ignore: [databaseFolderName, ...generatedFolders],
  });

  const filtered = results.filter((result) => {
    if (!shouldFilterRecency) return true;

    const updatedAt = result.stats?.updatedAt;

    // shouldn't happen
    if (!updatedAt) return false;

    const isRecentEnough = earliestUpdatedAt < updatedAt;

    return isRecentEnough;
  });

  const sorted =
    sort === "recent"
      ? filtered.sort((a, b) => {
          if (a.stats!.createdAt < b.stats!.createdAt) {
            return 1;
          }

          return -1;
        })
      : filtered;

  const projectRelativePaths = sorted
    .map((result) => result.path)
    .map((absolutePath) => makeRelative(absolutePath, projectRoot));
  return projectRelativePaths;
}

