getTodoPaths.rawText:
 async (
  config?: TodoPagesConfig
): Promise<string[]> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return [];

  const { recency, categoryStack, personId, priority, subExtension, sort } =
    destructureOptionalObject(config);

  /**
   * TODO:
   */
  const todayTimestamp = 0;
  const thisWeekTimestamp = 0;
  const earliestUpdatedAt =
    recency === "24-hours"
      ? Date.now() - 86400000
      : recency === "7-days"
      ? Date.now() - 86400000 * 7
      : recency === "today"
      ? todayTimestamp
      : recency === "this-week"
      ? thisWeekTimestamp
      : undefined;

  let todoPaths = await getProjectRelativePaths({
    type: "todo",
    filterPrivate: true,
    filterDraft: true,
    filterGenerated: true,
    earliestUpdatedAt,
    sort,
  });

  // filter on categoryStack, if it's there. Filter BEFORE getting the markdown frontmatter, that's slow...

  if (categoryStack && categoryStack.length === 0) {
    todoPaths = todoPaths?.filter((todoPath) => {
      const lastIndexTodoFolder = todoPath.lastIndexOf("todo/");

      if (lastIndexTodoFolder === -1) {
        return false;
      }

      const todoRelativeFileId = todoPath.slice(
        lastIndexTodoFolder + "todo/".length
      );

      const depth = todoRelativeFileId.split("/").length - 1;

      return depth === 0;
    });
  }
  if (categoryStack && categoryStack.length > 0) {
    todoPaths = todoPaths?.filter((todoPath) => {
      const lastIndexTodoFolder = todoPath.lastIndexOf("todo/");

      if (lastIndexTodoFolder === -1) {
        return false;
      }

      const todoRelativeFileId = todoPath.slice(
        lastIndexTodoFolder + "todo/".length
      );

      // console.log({ todoRelativeFileId });

      const categoryStackPrefix = categoryStack.join("/") + "/";

      if (!todoRelativeFileId.startsWith(categoryStackPrefix)) {
        return false;
      }

      return true;
    });
  }

  if (subExtension) {
    todoPaths = todoPaths?.filter((filePath) => {
      const filename = path.parse(filePath).base;
      const thisSubExtension = getSubExtension(filename);

      if (subExtension !== thisSubExtension) {
        return false;
      }

      return true;
    });
  }

  if (personId || priority) {
    // ONLY get the mapped object of all frontmatters if it's really needed. It may take a little longer... Filter AFTER applying filter for recency and categoryStack.
    const frontmatterObject = await getFrontmattersMappedObject(
      projectRoot,
      todoPaths
    );

    if (priority) {
      todoPaths = todoPaths?.filter((todoPath) => {
        const frontmatterPriority = frontmatterObject[todoPath]?.priority;

        const matchPriority = priority === String(frontmatterPriority);

        return matchPriority;
      });
    }

    if (personId) {
      todoPaths = todoPaths?.filter((todoPath) => {
        const frontmatterOwner_personId =
          frontmatterObject[todoPath]?.owner_personId;

        const matchPerson = personId === String(frontmatterOwner_personId);

        return matchPerson;
      });
    }
  }

  return todoPaths || [];
}

